INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Gestionnaire de projets', 'Planifie, organise et coordonne la réalisation de projets en respectant les contraintes de coûts, de délais et de qualité, et en assurant la gestion de l''équipe et des parties prenantes.', 800000, 'Gestion de projets, planification, leadership, communication, résolution de problèmes', 132, 'Définir les objectifs et la portée du projet, élaborer les plans, constituer l''équipe, gérer le budget et le calendrier, coordonner les tâches, communiquer avec les parties prenantes');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable des ressources humaines', 'Gère les processus liés à la gestion des ressources humaines, tels que le recrutement, la formation, la rémunération et les relations sociales, dans le but d''attirer, développer et retenir les talents au sein de l''organisation.', 900000, 'Gestion des ressources humaines, recrutement, formation, relations sociales, communication', 132, 'Définir les politiques RH, gérer les recrutements, élaborer les plans de formation, gérer les rémunérations, assurer le dialogue social, veiller au respect de la réglementation');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable administratif', 'Supervise et coordonne les activités administratives d''une organisation, telles que la gestion des installations, des approvisionnements, des budgets et du personnel administratif.', 700000, 'Gestion administrative, organisation, budgétisation, communication, leadership', 132, 'Gérer les installations et les approvisionnements, superviser le personnel administratif, coordonner les activités, élaborer et suivre les budgets, assurer la communication interne');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Archiviste', 'Gère et conserve les archives et les documents d''une organisation, en assurant leur classement, leur stockage, leur accessibilité et leur préservation.', 400000, 'Gestion documentaire, classement, conservation, recherche d''informations, respect des normes', 132, 'Classer et organiser les archives, assurer le stockage des documents, faciliter l''accès aux informations, veiller à la préservation des archives, respecter les normes et la réglementation');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Secrétaire', 'Assiste le personnel et les équipes dans leurs tâches administratives, telles que la rédaction de documents, la gestion des agendas, l''organisation de réunions et l''accueil des visiteurs.', 300000, 'Rédaction, organisation, communication, accueil, maîtrise des outils bureautiques', 132, 'Rédiger des documents, gérer les agendas, organiser des réunions, accueillir les visiteurs, assurer le suivi des tâches administratives');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Gestionnaire de contenu', 'Gère la création, la publication et la mise à jour du contenu numérique d''une organisation (site web, réseaux sociaux, etc.), en veillant à la cohérence et à la qualité des informations.', 600000, 'Gestion de contenu, rédaction web, référencement, communication numérique, maîtrise des outils web', 132, 'Créer et publier du contenu, assurer la mise à jour du site web, optimiser le référencement, animer les réseaux sociaux, analyser les performances');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Coordinateur d''événements', 'Organise et coordonne la logistique des événements (conférences, séminaires, salons, etc.) pour le compte d''une organisation, en gérant les aspects liés à la planification, aux ressources et à la communication.', 500000, 'Organisation d''événements, planification, gestion de ressources, communication, négociation', 132, 'Planifier les événements, réserver les lieux et les prestataires, gérer les budgets, coordonner la logistique, assurer la communication et la promotion');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable des services généraux', 'Gère les services de soutien d''une organisation, tels que l''entretien des locaux, la sécurité, le courrier, la restauration et les déplacements.', 600000, 'Gestion des services généraux, organisation, budgétisation, négociation, communication', 132, 'Superviser les services d''entretien, de sécurité et de restauration, gérer les budgets, négocier avec les prestataires, assurer la communication avec les utilisateurs');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable qualité', 'Élabore et met en œuvre le système de gestion de la qualité d''une organisation, veille au respect des normes et des réglementations, et assure l''amélioration continue des processus.', 700000, 'Gestion de la qualité, normes et réglementations, amélioration des processus, audit, communication', 132, 'Définir les politiques qualité, mettre en place le système de gestion, former le personnel, réaliser des audits, proposer des actions d''amélioration');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable de la conformité', 'Veille au respect des lois, réglementations et normes applicables dans une organisation, en mettant en place des procédures et des contrôles pour prévenir les risques de non-conformité.', 800000, 'Réglementation, gestion des risques, audit, communication, éthique', 132, 'Identifier les exigences réglementaires, élaborer les politiques de conformité, former le personnel, réaliser des audits, détecter les non-conformités, mettre en œuvre des actions correctives');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Analyste d''affaires', 'Analyse les besoins fonctionnels et opérationnels d''une organisation, identifie les solutions potentielles et accompagne leur mise en œuvre pour améliorer les processus et la performance.', 700000, 'Analyse des besoins, modélisation des processus, gestion des changements, communication, résolution de problèmes', 132, 'Analyser les besoins métier, modéliser les processus existants, identifier les solutions d''amélioration, accompagner la mise en œuvre, former les utilisateurs');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Consultant en organisation', 'Conseille les organisations sur l''optimisation de leur structure organisationnelle, de leurs processus et de leur performance, et accompagne la mise en œuvre des changements.', 1000000, 'Conseil en organisation, analyse des processus, gestion du changement, communication, leadership', 132, 'Analyser l''organisation et les processus, identifier les axes d''amélioration, proposer des solutions, accompagner la mise en œuvre des changements, former les équipes');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Formateur', 'Conçoit et anime des sessions de formation pour développer les compétences du personnel d''une organisation, dans divers domaines tels que les techniques métier, le management ou le développement personnel.', 600000, 'Conception de formations, animation de sessions, pédagogie, communication, expertise métier', 132, 'Identifier les besoins en formation, concevoir les programmes, préparer les supports, animer les sessions, évaluer les acquis');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Assistant de Gestion', 'L’assistant de gestion contribue à la gestion administrative et comptable d’une entreprise. Il assiste les dirigeants dans le suivi des dossiers, la gestion des plannings et la communication interne.', null, 800000, 'Gestion administrative, Comptabilité, Communication, Organisation', 133, 'Suivi des dossiers, Gestion des plannings, Communication interne'),
    ('Chargé de Clientèle', 'Le chargé de clientèle est en charge de la relation entre l’entreprise et ses clients. Il répond à leurs demandes, traite les réclamations et s’assure de leur satisfaction.', null, 900000, 'Relation client, Communication, Résolution de problèmes', 133, 'Répondre aux demandes des clients, Traiter les réclamations, Assurer la satisfaction client'),
    ('Gestionnaire des Ressources Humaines', 'Le gestionnaire des ressources humaines s’occupe du recrutement, de la formation, de la gestion des contrats et des relations sociales au sein de l’entreprise.', null, 1200000, 'Gestion des RH, Recrutement, Formation, Droit du travail', 133, 'Recrutement, Formation, Gestion des contrats, Relations sociales'),
    ('Assistant Marketing', 'L’assistant marketing participe à la conception et à la mise en œuvre des actions marketing. Il réalise des études de marché, participe à la création de supports de communication et assure le suivi des campagnes.', null, 850000, 'Marketing, Communication, Études de marché', 133, 'Conception des actions marketing, Réalisation d’études de marché, Suivi des campagnes'),
    ('Chargé de Projet Informatique', 'Le chargé de projet informatique coordonne les équipes de développement, élabore les cahiers des charges et assure le suivi des projets informatiques au sein de l’entreprise.', null, 1500000, 'Gestion de projet, Informatique, Communication', 133, 'Coordination des équipes, Élaboration des cahiers des charges, Suivi des projets informatiques'),
    ('Secrétaire Médical', 'Le/la secrétaire médical assure la gestion administrative des dossiers médicaux, planifie les rendez-vous et assure la liaison entre les patients et les professionnels de santé.', null, 950000, 'Gestion administrative, Secrétariat médical, Communication', 133, 'Gestion des dossiers médicaux, Planification des rendez-vous, Liaison avec les professionnels de santé'),
    ('Responsable Logistique', 'Le responsable logistique organise et coordonne les opérations liées à la chaîne d’approvisionnement. Il veille à la gestion optimale des stocks et à la satisfaction des clients en termes de livraison.', null, 1300000, 'Logistique, Gestion des stocks, Organisation', 133, 'Organisation des opérations logistiques, Gestion des stocks, Satisfaction des clients en termes de livraison'),
    ('Conseiller(ère) en Assurance', 'Le conseiller(ère) en assurance propose des produits d’assurance adaptés aux besoins des clients, les informe sur les garanties et les assiste en cas de sinistre.', null, 1000000, 'Assurance, Conseil client, Gestion des sinistres', 133, 'Proposition des produits d’assurance, Informations sur les garanties, Assistance en cas de sinistre'),
    ('Spécialiste en Systèmes d’Information', 'Le spécialiste en systèmes d’information développe, met en place et assure la maintenance des systèmes informatiques au sein de l’entreprise. Il résout les problèmes techniques liés aux systèmes.', null, 1600000, 'Informatique, Développement, Maintenance', 133, 'Développement des systèmes informatiques, Mise en place, Maintenance, Résolution des problèmes techniques'),
    ('Assistant de Communication', 'L’assistant de communication participe à la conception des supports de communication, gère les relations avec les médias et assure la promotion de l’image de l’entreprise.', null, 900000, 'Communication, Relations publiques, Promotion', 133, 'Conception des supports de communication, Gestion des relations avec les médias, Promotion de l’image de l’entreprise'),
    ('Chargé de Recouvrement', 'Le chargé de recouvrement est chargé de récupérer les créances impayées. Il négocie des solutions avec les débiteurs et assure le suivi des échéances de paiement.', null, 950000, 'Recouvrement, Négociation, Suivi des échéances', 133, 'Récupération des créances impayées, Négociation avec les débiteurs, Suivi des échéances de paiement'),
    ('Analyste Financier(ère)', 'L’analyste financier(ère) analyse les données financières, évalue les investissements potentiels et propose des recommandations pour optimiser la performance financière de l’entreprise.', null, 1400000, 'Analyse financière, Évaluation des investissements, Recommandations', 133, 'Analyse des données financières, Évaluation des investissements, Recommandations pour optimiser la performance financière'),
    ('Assistant de Direction', 'L’assistant de direction assure le secrétariat de la direction générale, organise les déplacements, prépare les réunions et assure la gestion du temps de la direction.', null, 1100000, 'Secrétariat de direction, Organisation, Gestion du temps', 133, 'Organisation des déplacements, Préparation des réunions, Gestion du temps de la direction'),
    ('Responsable Commercial', 'Le responsable commercial élabore la stratégie commerciale, encadre les équipes de vente, négocie des contrats et assure le développement du portefeuille clients.', null, 1300000, 'Stratégie commerciale, Encadrement des équipes, Négociation', 133, 'Élaboration de la stratégie commerciale, Encadrement des équipes de vente, Négociation des contrats, Développement du portefeuille clients'),
    ('Technicien(ne) en Support Informatique', 'Le technicien(ne) en support informatique assure l’assistance technique aux utilisateurs, résout les problèmes liés aux logiciels et matériels informatiques, et assure la maintenance des équipements.', null, 1000000, 'Support informatique, Résolution de problèmes, Maintenance', 133, 'Assistance technique aux utilisateurs, Résolution des problèmes informatiques, Maintenance des équipements');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Ingénieur en Énergie Renouvelable', 'L’ingénieur en énergie renouvelable conçoit, développe et supervise des projets liés aux énergies renouvelables tels que l’énergie solaire, éolienne et hydraulique.', null, 1800000, 'Énergie renouvelable, Conception de projets, Supervision', 134, 'Conception de projets liés aux énergies renouvelables, Développement, Supervision des projets'),
    ('Technicien(ne) en Génie Thermique', 'Le technicien(ne) en génie thermique réalise des études thermiques, conçoit des systèmes de chauffage et de climatisation, et assure la maintenance des installations.', null, 1200000, 'Génie thermique, Études thermiques, Maintenance', 134, 'Réalisation d’études thermiques, Conception de systèmes de chauffage et climatisation, Maintenance des installations'),
    ('Chef de Projet en Génie Énergétique', 'Le chef de projet en génie énergétique coordonne les équipes, élabore les plans de projet, gère le budget et assure la mise en œuvre de solutions énergétiques efficaces.', null, 1600000, 'Génie énergétique, Coordination de projets, Gestion budgétaire', 134, 'Coordination des équipes, Élaboration des plans de projet, Gestion du budget, Mise en œuvre de solutions énergétiques'),
    ('Technicien(ne) en Énergies Renouvelables', 'Le technicien(ne) en énergies renouvelables installe, entretient et répare des équipements utilisant des sources d’énergie renouvelable, tels que les panneaux solaires et les éoliennes.', null, 1000000, 'Énergies renouvelables, Installation, Maintenance', 134, 'Installation d’équipements utilisant des énergies renouvelables, Entretien, Réparation'),
    ('Expert en Systèmes Énergétiques', 'L’expert en systèmes énergétiques analyse les besoins énergétiques des entreprises, propose des solutions d’optimisation et met en place des systèmes pour améliorer l’efficacité énergétique.', null, 2000000, 'Systèmes énergétiques, Analyse des besoins, Optimisation', 134, 'Analyse des besoins énergétiques, Proposition de solutions d’optimisation, Mise en place de systèmes pour améliorer l’efficacité énergétique'),
    ('Technicien(ne) en Climatisation', 'Le technicien(ne) en climatisation installe, entretient et répare les systèmes de climatisation et de ventilation pour assurer un confort thermique dans les bâtiments.', null, 1100000, 'Climatisation, Installation, Entretien', 134, 'Installation des systèmes de climatisation et ventilation, Entretien, Réparation'),
    ('Chargé de Projets Énergétiques', 'Le chargé de projets énergétiques gère les projets visant à améliorer l’efficacité énergétique, coordonne les équipes et assure le suivi des budgets et des délais.', null, 1500000, 'Projets énergétiques, Coordination, Gestion de budgets', 134, 'Gestion des projets d’amélioration de l’efficacité énergétique, Coordination des équipes, Suivi des budgets et des délais'),
    ('Ingénieur Thermicien(ne)', 'L’ingénieur thermicien(ne) conçoit des systèmes de chauffage et de climatisation, réalise des simulations thermiques, et propose des solutions pour optimiser l’efficacité énergétique.', null, 1700000, 'Thermique, Conception de systèmes, Simulation thermique', 134, 'Conception de systèmes de chauffage et climatisation, Réalisation de simulations thermiques, Proposition de solutions pour optimiser l’efficacité énergétique'),
    ('Technicien(ne) en Économies d’Énergie', 'Le technicien(ne) en économies d’énergie réalise des audits énergétiques, identifie les sources de gaspillage énergétique et propose des mesures pour réduire la consommation d’énergie.', null, 1300000, 'Économies d’énergie, Audits énergétiques, Identification des sources de gaspillage', 134, 'Réalisation d’audits énergétiques, Identification des sources de gaspillage énergétique, Proposition de mesures pour réduire la consommation d’énergie'),
    ('Responsable de Maintenance Thermique', 'Le responsable de maintenance thermique organise et supervise les activités de maintenance préventive et corrective des installations de chauffage et de climatisation.', null, 1400000, 'Maintenance thermique, Supervision, Organisation', 134, 'Organisation des activités de maintenance, Supervision des équipes, Maintenance préventive et corrective'),
    ('Consultant en Énergie', 'Le/la consultant en énergie conseille les entreprises sur les solutions pour optimiser leur consommation énergétique, réalise des études de faisabilité et propose des recommandations.', null, 1600000, 'Conseil en énergie, Études de faisabilité, Recommandations', 134, 'Conseils sur les solutions d’optimisation de la consommation énergétique, Réalisation d’études de faisabilité, Proposition de recommandations'),
    ('Technicien(ne) de Maintenance des Énergies Renouvelables', 'Le technicien(ne) de maintenance des énergies renouvelables assure la maintenance des équipements utilisant des sources d’énergie renouvelable comme les parcs éoliens et les centrales solaires.', null, 1200000, 'Maintenance des énergies renouvelables, Équipements, Parcs éoliens, Centrales solaires', 134, 'Maintenance des équipements utilisant des énergies renouvelables, Entretien des parcs éoliens, Centrales solaires'),
    ('Chargé de Développement Énergétique', 'Le chargé de développement énergétique identifie les opportunités de projets liés à l’énergie, évalue leur faisabilité, et participe à la conception et à la mise en œuvre.', null, 1400000, 'Développement énergétique, Évaluation de faisabilité, Conception et mise en œuvre', 134, 'Identification des opportunités de projets liés à l’énergie, Évaluation de faisabilité, Conception et mise en œuvre de projets'),
    ('Technicien(ne) en Gestion de l’Énergie', 'Le technicien(ne) en gestion de l’énergie surveille la consommation énergétique des bâtiments, propose des solutions pour optimiser l’utilisation de l’énergie et assure le suivi des indicateurs de performance.', null, 1100000, 'Gestion de l’énergie, Surveillance de la consommation, Optimisation', 134, 'Surveillance de la consommation énergétique, Proposition de solutions d’optimisation, Suivi des indicateurs de performance');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Ingénieur en Biotechnologie', 'L’ingénieur en biotechnologie conçoit, développe et met en œuvre des procédés biotechnologiques pour la production de médicaments, d’enzymes ou d’autres produits biologiques.', null, 2000000, 'Biotechnologie, Conception de procédés, Mise en œuvre', 135, 'Conception de procédés biotechnologiques, Développement, Mise en œuvre'),
    ('Technicien(ne) en Technologie Alimentaire', 'Le technicien(ne) en technologie alimentaire participe à la conception et à la fabrication de produits alimentaires en suivant les normes de qualité et d’hygiène.', null, 1000000, 'Technologie alimentaire, Conception de produits, Fabrication', 135, 'Conception de produits alimentaires, Fabrication, Respect des normes de qualité et d’hygiène'),
    ('Chercheur en Biotechnologie', 'Le chercheur en biotechnologie mène des recherches pour découvrir de nouvelles applications biotechnologiques, développer des médicaments ou améliorer les procédés existants.', null, 2200000, 'Recherche en biotechnologie, Développement, Amélioration des procédés', 135, 'Recherche de nouvelles applications biotechnologiques, Développement de médicaments, Amélioration des procédés existants'),
    ('Ingénieur en Agroalimentaire', 'L’ingénieur en agroalimentaire participe à la conception, à la fabrication et à l’amélioration des produits alimentaires en tenant compte des normes de qualité et de sécurité.', null, 1800000, 'Agroalimentaire, Conception de produits, Amélioration', 135, 'Conception et fabrication des produits alimentaires, Amélioration des processus, Respect des normes de qualité et de sécurité'),
    ('Technicien(ne) de Laboratoire en Biotechnologie', 'Le technicien(ne) de laboratoire en biotechnologie réalise des analyses, des tests et des expériences pour soutenir les travaux de recherche et de développement en biotechnologie.', null, 1200000, 'Laboratoire, Analyses, Recherche et développement', 135, 'Réalisation d’analyses et de tests, Soutien à la recherche et au développement en biotechnologie'),
    ('Chef de Projet en Technologie Alimentaire', 'Le chef de projet en technologie alimentaire coordonne les différentes phases de développement des produits alimentaires, de la conception à la mise sur le marché.', null, 1600000, 'Gestion de projet, Technologie alimentaire, Coordination', 135, 'Coordination des phases de développement des produits alimentaires, Gestion de projet, Mise sur le marché'),
    ('Technicien(ne) en Biologie Moléculaire', 'Le technicien(ne) en biologie moléculaire réalise des manipulations et des analyses moléculaires, notamment dans le domaine de la génétique, pour contribuer à la recherche biotechnologique.', null, 1300000, 'Biologie moléculaire, Analyses, Recherche biotechnologique', 135, 'Réalisation de manipulations et d’analyses moléculaires, Contribution à la recherche biotechnologique'),
    ('Responsable Qualité en Industrie Alimentaire', 'Le responsable qualité en industrie alimentaire veille au respect des normes de qualité, élabore et met en place les procédures de contrôle, et coordonne les actions d’amélioration continue.', null, 1500000, 'Qualité alimentaire, Contrôle, Amélioration continue', 135, 'Veille au respect des normes de qualité, Élaboration et mise en place des procédures de contrôle, Coordination des actions d’amélioration continue'),
    ('Technicien(ne) en Microbiologie', 'Le technicien(ne) en microbiologie réalise des analyses microbiologiques pour contrôler la qualité sanitaire des produits alimentaires ou contribuer à la recherche en biotechnologie.', null, 1100000, 'Microbiologie, Analyses microbiologiques, Contrôle qualité', 135, 'Réalisation d’analyses microbiologiques, Contrôle de la qualité sanitaire des produits alimentaires'),
    ('Chef de Projet en Biotechnologie Médicale', 'Le chef de projet en biotechnologie médicale coordonne les projets de développement de produits biotechnologiques à usage médical, de la conception à la mise sur le marché.', null, 1800000, 'Biotechnologie médicale, Gestion de projet, Coordination', 135, 'Coordination des projets de développement de produits biotechnologiques médicaux, Gestion de projet, Mise sur le marché'),
    ('Technicien(ne) en Sécurité Alimentaire', 'Le technicien(ne) en sécurité alimentaire met en place des mesures de contrôle et d’hygiène, réalise des audits pour assurer la sécurité sanitaire des produits alimentaires.', null, 1300000, 'Sécurité alimentaire, Contrôle, Audits', 135, 'Mise en place de mesures de contrôle et d’hygiène, Réalisation d’audits pour assurer la sécurité sanitaire des produits alimentaires'),
    ('Chargé de Recherche en Sciences Alimentaires', 'Le chargé de recherche en sciences alimentaires participe à des projets de recherche visant à améliorer la qualité des produits alimentaires et à développer de nouvelles technologies dans ce domaine.', null, 1600000, 'Recherche en sciences alimentaires, Amélioration de la qualité, Développement technologique', 135, 'Participation à des projets de recherche, Amélioration de la qualité des produits alimentaires, Développement de nouvelles technologies'),
    ('Technicien(ne) en Génie Biologique', 'Le technicien(ne) en génie biologique participe à la mise en place et à la gestion des procédés biologiques, notamment dans les secteurs de la santé et de l’environnement.', null, 1200000, 'Génie biologique, Mise en place des procédés, Gestion', 135, 'Participation à la mise en place et à la gestion des procédés biologiques, Intervention dans les secteurs de la santé et de l’environnement'),
    ('Chef de Produit en Technologie Alimentaire', 'Le chef de produit en technologie alimentaire participe à la conception de nouveaux produits alimentaires, élabore des stratégies de lancement et assure le suivi de la performance sur le marché.', null, 1700000, 'Produit alimentaire, Conception, Stratégie de lancement', 135, 'Conception de nouveaux produits alimentaires, Élaboration de stratégies de lancement, Suivi de la performance sur le marché');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
