INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Ingénieur en Génie Civil', 'L’ingénieur en génie civil conçoit, supervise et coordonne les projets de construction d’infrastructures telles que les routes, les ponts, les bâtiments, en veillant à leur conception structurale et à la conformité aux normes.', null, 2000000, 'Génie civil, Conception, Coordination de projets', 137, 'Conception, supervision et coordination de projets de construction d’infrastructures, Veille à la conception structurale et à la conformité aux normes'),
    ('Conducteur de Travaux en Génie Civil', 'Le conducteur de travaux en génie civil assure la gestion opérationnelle des chantiers, coordonne les équipes, et veille à la mise en œuvre des plans et des normes de construction.', null, 1500000, 'Travaux en génie civil, Gestion opérationnelle, Coordination des équipes', 137, 'Gestion opérationnelle des chantiers, Coordination des équipes, Mise en œuvre des plans et des normes de construction'),
