INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Gestionnaire de portefeuille', 'Gère les investissements et les portefeuilles d''actifs financiers pour le compte de clients institutionnels ou individuels, conformément à leurs objectifs et leur tolérance au risque.', 1200000, 'Gestion de portefeuille, analyse financière, connaissance des marchés, prise de décision, gestion des risques', 122, 'Définir la stratégie d''investissement, sélectionner les actifs, répartir les investissements, surveiller et rééquilibrer le portefeuille, communiquer avec les clients');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Contrôleur de gestion', 'Suit les performances financières et opérationnelles d''une entreprise, analyse les écarts entre les résultats réels et les objectifs, et propose des mesures correctives pour optimiser la rentabilité.', 600000, 'Analyse financière, reporting, budgétisation, contrôle de gestion, communication', 122, 'Élaborer les budgets, suivre les indicateurs de performance, analyser les écarts, proposer des plans d''action, rédiger des rapports de gestion');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Auditeur financier', 'Examine les états financiers et les opérations d''une entreprise pour s''assurer de leur conformité avec les normes comptables et réglementaires en vigueur, et émet un avis sur la fiabilité des comptes.', 800000, 'Audit financier, normes comptables, réglementation, analyse de risques, communication', 122, 'Planifier et exécuter les missions d''audit, examiner les documents comptables, évaluer les contrôles internes, détecter les anomalies, rédiger les rapports d''audit');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Économiste d’entreprise', 'Analyse l''environnement économique, les tendances du marché et les facteurs susceptibles d''impacter les activités de l''entreprise, et fournit des recommandations stratégiques pour optimiser les décisions.', 700000, 'Analyse économique, modélisation, prévision, communication, pensée stratégique', 122, 'Suivre les indicateurs économiques, analyser les tendances du marché, évaluer les impacts sur l''entreprise, élaborer des scénarios prospectifs, formuler des recommandations stratégiques');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Consultant en stratégie', 'Aide les entreprises à définir et mettre en œuvre leur stratégie en analysant leur environnement, leurs forces et faiblesses, et en proposant des solutions pour améliorer leur compétitivité et leur rentabilité.', 1000000, 'Analyse stratégique, résolution de problèmes, pensée critique, communication, gestion de projets', 122, 'Réaliser des études stratégiques, identifier les opportunités et les menaces, proposer des plans d''action, accompagner la mise en œuvre des stratégies, former les équipes');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable des ressources humaines', 'Gère les processus liés à la gestion des ressources humaines, tels que le recrutement, la formation, la rémunération et les relations sociales, dans le but d''attirer, développer et retenir les talents au sein de l''entreprise.', 800000, 'Gestion des ressources humaines, recrutement, formation, relations sociales, communication', 122, 'Définir les politiques RH, gérer les recrutements, élaborer les plans de formation, gérer les rémunérations, assurer le dialogue social, veiller au respect de la réglementation');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Chef de projet', 'Planifie, organise et coordonne la réalisation de projets en respectant les contraintes de coûts, de délais et de qualité, et en assurant la gestion de l''équipe et des parties prenantes.', 700000, 'Gestion de projets, planification, leadership, communication, résolution de problèmes', 122, 'Définir les objectifs et la portée du projet, élaborer les plans, constituer l''équipe, gérer le budget et le calendrier, coordonner les tâches, communiquer avec les parties prenantes');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Expert en marketing', 'Conçoit et met en œuvre des stratégies marketing pour promouvoir les produits ou services d''une entreprise, en analysant les besoins des clients, la concurrence et les tendances du marché.', 600000, 'Marketing, études de marché, stratégie, communication, créativité', 122, 'Réaliser des études de marché, élaborer les plans marketing, définir le positionnement et la stratégie, gérer les campagnes promotionnelles, analyser les résultats');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Négociateur commercial', 'Négocie les conditions commerciales avec les clients, fournisseurs ou partenaires, dans le but d''obtenir les meilleures conditions pour l''entreprise en termes de prix, qualité et délais.', 500000, 'Négociation, communication, persuasion, analyse, gestion des conflits', 122, 'Préparer les négociations, défendre les intérêts de l''entreprise, trouver des solutions gagnant-gagnant, conclure les accords, assurer le suivi');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Entrepreneur', 'Identifie des opportunités d''affaires, élabore un plan d''entreprise, mobilise les ressources nécessaires et crée une nouvelle entreprise pour exploiter ces opportunités.', 'Variable', 'Esprit d''entreprise, créativité, prise de risques, leadership, gestion de projets', 122, 'Identifier une idée d''entreprise, réaliser une étude de marché, élaborer un business plan, trouver des financements, lancer l''activité, gérer la croissance');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Conseiller financier', 'Conseille les particuliers ou les entreprises sur la gestion de leur patrimoine, leurs investissements et leurs plans d''épargne, en fonction de leurs objectifs et de leur profil de risque.', 400000, 'Planification financière, gestion de patrimoine, connaissances financières, communication, éthique', 122, 'Analyser la situation financière des clients, définir leurs objectifs, proposer des solutions d''investissement, gérer les portefeuilles, assurer un suivi régulier');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Économiste bancaire', 'Analyse les tendances économiques et financières pour aider les banques à prendre des décisions éclairées en matière de prêts, de taux d''intérêt et de gestion des risques.', 800000, 'Analyse économique, prévision, modélisation, connaissances bancaires, communication', 122, 'Suivre les indicateurs économiques, modéliser les scénarios, prévoir les impacts sur le secteur bancaire, évaluer les risques, formuler des recommandations stratégiques');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable de la conformité', 'Veille au respect des lois, réglementations et normes applicables dans le domaine financier, en mettant en place des procédures et des contrôles pour prévenir les risques de non-conformité.', 700000, 'Réglementation financière, gestion des risques, audit, communication, éthique', 122, 'Identifier les exigences réglementaires, élaborer les politiques de conformité, former le personnel, réaliser des audits, détecter les non-conformités, mettre en œuvre des actions correctives');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Analyste politique', 'Analyse les enjeux politiques, les politiques publiques et les tendances géopolitiques pour fournir des recommandations stratégiques aux décideurs politiques, aux organisations et aux entreprises.', 600000, 'Analyse politique, recherche, rédaction, communication, pensée critique', 123, 'Suivre l''actualité politique, analyser les enjeux, rédiger des notes d''analyse, formuler des recommandations, communiquer avec les parties prenantes');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Conseiller politique', 'Conseille les élus, les partis politiques ou les organisations sur des questions stratégiques, en leur fournissant une expertise politique, en développant des stratégies de communication et en gérant les relations avec les parties prenantes.', 800000, 'Conseil stratégique, communication politique, gestion de campagnes, relations publiques, négociation', 123, 'Élaborer des stratégies politiques, préparer les discours et les interventions, gérer les relations avec les médias, négocier avec les parties prenantes, coordonner les campagnes');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Diplomate', 'Représente et défend les intérêts de son pays à l''étranger, négocie des accords internationaux, favorise la coopération entre les nations et contribue à la résolution des conflits.', 1000000, 'Relations internationales, négociation, communication interculturelle, protocole, langues étrangères', 123, 'Représenter le pays à l''étranger, négocier des accords, promouvoir la coopération, gérer les crises, assurer la protection des ressortissants');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Lobbyiste', 'Défend les intérêts d''une organisation ou d''un groupe auprès des décideurs politiques, en menant des actions de lobbying et en influençant le processus décisionnel.', 700000, 'Lobbying, relations publiques, communication, négociation, connaissance du cadre législatif', 123, 'Identifier les enjeux et les parties prenantes, élaborer des stratégies de lobbying, mener des campagnes d''influence, établir des relations avec les décideurs, défendre les intérêts des clients');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Chargé de mission', 'Travaille sur des projets spécifiques pour le compte d''organisations publiques ou privées, en menant des études, en coordonnant des équipes et en proposant des recommandations.', 500000, 'Gestion de projets, recherche, analyse, rédaction, travail en équipe', 123, 'Définir les objectifs de la mission, réaliser des études, coordonner les équipes, rédiger des rapports, formuler des recommandations');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Consultant en politiques publiques', 'Conseille les gouvernements, les organisations internationales et les entreprises sur l''élaboration, la mise en œuvre et l''évaluation des politiques publiques dans divers domaines.', 800000, 'Analyse des politiques publiques, conseil stratégique, expertise sectorielle, communication, gestion de projets', 123, 'Réaliser des études d''impact, analyser les enjeux, proposer des solutions, accompagner la mise en œuvre, évaluer les résultats');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Journaliste politique', 'Couvre l''actualité politique, analyse les enjeux et les événements politiques, et informe le public à travers différents médias (presse écrite, radio, télévision, web).', 400000, 'Rédaction, recherche d''informations, analyse politique, communication, déontologie journalistique', 123, 'Suivre l''actualité politique, réaliser des enquêtes, interviewer des sources, rédiger des articles, animer des émissions');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Attaché parlementaire', 'Assiste les élus parlementaires dans leurs activités législatives et de représentation, en effectuant des recherches, en préparant des dossiers et en gérant les relations avec les parties prenantes.', 500000, 'Recherche législative, rédaction, communication, gestion de projets, relations publiques', 123, 'Préparer les dossiers législatifs, rédiger des notes de synthèse, organiser les activités de l''élu, gérer les relations avec les citoyens et les groupes d''intérêt');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Spécialiste des relations internationales', 'Analyse les enjeux géopolitiques, les relations entre les États et les organisations internationales, et fournit des conseils stratégiques aux gouvernements, aux entreprises et aux ONG.', 700000, 'Géopolitique, diplomatie, négociation, langues étrangères, communication interculturelle', 123, 'Suivre l''actualité internationale, analyser les enjeux, rédiger des rapports, formuler des recommandations, faciliter les échanges internationaux');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Médiateur', 'Facilite la résolution des conflits entre parties en désaccord, en favorisant le dialogue, la compréhension mutuelle et la recherche de solutions équitables.', 600000, 'Médiation, négociation, communication, gestion des conflits, éthique', 123, 'Établir un climat de confiance, écouter les parties, clarifier les points de vue, explorer les options, proposer des solutions acceptables');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable des affaires publiques', 'Gère les relations entre une organisation et les autorités publiques, en défendant les intérêts de l''organisation et en influençant les processus décisionnels.', 800000, 'Relations publiques, lobbying, communication, négociation, connaissance du cadre réglementaire', 123, 'Identifier les enjeux clés, établir des relations avec les décideurs, mener des actions de lobbying, défendre les intérêts de l''organisation, assurer la veille réglementaire');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Professeur de sciences politiques', 'Enseigne les théories, les concepts et les méthodes de recherche en sciences politiques dans des établissements d''enseignement supérieur, tout en menant des activités de recherche académique.', 600000, 'Expertise en sciences politiques, pédagogie, recherche, publication, encadrement d''étudiants', 123, 'Préparer et dispenser des cours, encadrer des travaux d''étudiants, mener des recherches, publier des articles scientifiques, participer à des conférences');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Chercheur en sciences politiques', 'Mène des recherches approfondies sur des sujets liés à la politique, aux institutions et aux comportements politiques, en utilisant des méthodes scientifiques et en contribuant à l''avancement des connaissances dans ce domaine.', 700000, 'Recherche scientifique, analyse de données, rédaction, publication, présentation', 123, 'Définir des problématiques de recherche, collecter et analyser des données, développer des théories, rédiger des articles scientifiques, présenter les résultats');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Consultant en sécurité internationale', 'Conseille les gouvernements, les organisations internationales et les entreprises sur les questions de sécurité mondiale, telles que les conflits, le terrorisme, la cybersécurité et la gestion des crises.', 900000, 'Géopolitique, sécurité, analyse de risques, stratégie, communication', 123, 'Analyser les menaces sécuritaires, évaluer les risques, élaborer des stratégies de sécurité, former les équipes, gérer les situations de crise');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Avocat', 'Conseille et représente ses clients dans des affaires juridiques, plaide devant les tribunaux et négocie des transactions dans différents domaines du droit.', 800000, 'Connaissances juridiques, plaidoirie, négociation, rédaction, communication', 333, 'Conseiller les clients, préparer les dossiers, plaider devant les tribunaux, rédiger des actes juridiques, négocier des transactions');
