INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Responsable de la conformité', 'Veille au respect des lois, réglementations et normes applicables dans une organisation, en mettant en place des procédures et des contrôles pour prévenir les risques de non-conformité.', 800000, 'Réglementation, gestion des risques, audit, communication, éthique', 132, 'Identifier les exigences réglementaires, élaborer les politiques de conformité, former le personnel, réaliser des audits, détecter les non-conformités, mettre en œuvre des actions correctives');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Analyste d''affaires', 'Analyse les besoins fonctionnels et opérationnels d''une organisation, identifie les solutions potentielles et accompagne leur mise en œuvre pour améliorer les processus et la performance.', 700000, 'Analyse des besoins, modélisation des processus, gestion des changements, communication, résolution de problèmes', 132, 'Analyser les besoins métier, modéliser les processus existants, identifier les solutions d''amélioration, accompagner la mise en œuvre, former les utilisateurs');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Consultant en organisation', 'Conseille les organisations sur l''optimisation de leur structure organisationnelle, de leurs processus et de leur performance, et accompagne la mise en œuvre des changements.', 1000000, 'Conseil en organisation, analyse des processus, gestion du changement, communication, leadership', 132, 'Analyser l''organisation et les processus, identifier les axes d''amélioration, proposer des solutions, accompagner la mise en œuvre des changements, former les équipes');
INSERT INTO Metiers (Nom, Description, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES ('Formateur', 'Conçoit et anime des sessions de formation pour développer les compétences du personnel d''une organisation, dans divers domaines tels que les techniques métier, le management ou le développement personnel.', 600000, 'Conception de formations, animation de sessions, pédagogie, communication, expertise métier', 132, 'Identifier les besoins en formation, concevoir les programmes, préparer les supports, animer les sessions, évaluer les acquis');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Assistant de Gestion', 'L’assistant de gestion contribue à la gestion administrative et comptable d’une entreprise. Il assiste les dirigeants dans le suivi des dossiers, la gestion des plannings et la communication interne.', null, 800000, 'Gestion administrative, Comptabilité, Communication, Organisation', 133, 'Suivi des dossiers, Gestion des plannings, Communication interne'),
    ('Chargé de Clientèle', 'Le chargé de clientèle est en charge de la relation entre l’entreprise et ses clients. Il répond à leurs demandes, traite les réclamations et s’assure de leur satisfaction.', null, 900000, 'Relation client, Communication, Résolution de problèmes', 133, 'Répondre aux demandes des clients, Traiter les réclamations, Assurer la satisfaction client'),
    ('Gestionnaire des Ressources Humaines', 'Le gestionnaire des ressources humaines s’occupe du recrutement, de la formation, de la gestion des contrats et des relations sociales au sein de l’entreprise.', null, 1200000, 'Gestion des RH, Recrutement, Formation, Droit du travail', 133, 'Recrutement, Formation, Gestion des contrats, Relations sociales'),
    ('Assistant Marketing', 'L’assistant marketing participe à la conception et à la mise en œuvre des actions marketing. Il réalise des études de marché, participe à la création de supports de communication et assure le suivi des campagnes.', null, 850000, 'Marketing, Communication, Études de marché', 133, 'Conception des actions marketing, Réalisation d’études de marché, Suivi des campagnes'),
    ('Chargé de Projet Informatique', 'Le chargé de projet informatique coordonne les équipes de développement, élabore les cahiers des charges et assure le suivi des projets informatiques au sein de l’entreprise.', null, 1500000, 'Gestion de projet, Informatique, Communication', 133, 'Coordination des équipes, Élaboration des cahiers des charges, Suivi des projets informatiques'),
    ('Secrétaire Médical', 'Le/la secrétaire médical assure la gestion administrative des dossiers médicaux, planifie les rendez-vous et assure la liaison entre les patients et les professionnels de santé.', null, 950000, 'Gestion administrative, Secrétariat médical, Communication', 133, 'Gestion des dossiers médicaux, Planification des rendez-vous, Liaison avec les professionnels de santé'),
    ('Responsable Logistique', 'Le responsable logistique organise et coordonne les opérations liées à la chaîne d’approvisionnement. Il veille à la gestion optimale des stocks et à la satisfaction des clients en termes de livraison.', null, 1300000, 'Logistique, Gestion des stocks, Organisation', 133, 'Organisation des opérations logistiques, Gestion des stocks, Satisfaction des clients en termes de livraison'),
    ('Conseiller(ère) en Assurance', 'Le conseiller(ère) en assurance propose des produits d’assurance adaptés aux besoins des clients, les informe sur les garanties et les assiste en cas de sinistre.', null, 1000000, 'Assurance, Conseil client, Gestion des sinistres', 133, 'Proposition des produits d’assurance, Informations sur les garanties, Assistance en cas de sinistre'),
    ('Spécialiste en Systèmes d’Information', 'Le spécialiste en systèmes d’information développe, met en place et assure la maintenance des systèmes informatiques au sein de l’entreprise. Il résout les problèmes techniques liés aux systèmes.', null, 1600000, 'Informatique, Développement, Maintenance', 133, 'Développement des systèmes informatiques, Mise en place, Maintenance, Résolution des problèmes techniques'),
    ('Assistant de Communication', 'L’assistant de communication participe à la conception des supports de communication, gère les relations avec les médias et assure la promotion de l’image de l’entreprise.', null, 900000, 'Communication, Relations publiques, Promotion', 133, 'Conception des supports de communication, Gestion des relations avec les médias, Promotion de l’image de l’entreprise'),
    ('Chargé de Recouvrement', 'Le chargé de recouvrement est chargé de récupérer les créances impayées. Il négocie des solutions avec les débiteurs et assure le suivi des échéances de paiement.', null, 950000, 'Recouvrement, Négociation, Suivi des échéances', 133, 'Récupération des créances impayées, Négociation avec les débiteurs, Suivi des échéances de paiement'),
    ('Analyste Financier(ère)', 'L’analyste financier(ère) analyse les données financières, évalue les investissements potentiels et propose des recommandations pour optimiser la performance financière de l’entreprise.', null, 1400000, 'Analyse financière, Évaluation des investissements, Recommandations', 133, 'Analyse des données financières, Évaluation des investissements, Recommandations pour optimiser la performance financière'),
    ('Assistant de Direction', 'L’assistant de direction assure le secrétariat de la direction générale, organise les déplacements, prépare les réunions et assure la gestion du temps de la direction.', null, 1100000, 'Secrétariat de direction, Organisation, Gestion du temps', 133, 'Organisation des déplacements, Préparation des réunions, Gestion du temps de la direction'),
    ('Responsable Commercial', 'Le responsable commercial élabore la stratégie commerciale, encadre les équipes de vente, négocie des contrats et assure le développement du portefeuille clients.', null, 1300000, 'Stratégie commerciale, Encadrement des équipes, Négociation', 133, 'Élaboration de la stratégie commerciale, Encadrement des équipes de vente, Négociation des contrats, Développement du portefeuille clients'),
    ('Technicien(ne) en Support Informatique', 'Le technicien(ne) en support informatique assure l’assistance technique aux utilisateurs, résout les problèmes liés aux logiciels et matériels informatiques, et assure la maintenance des équipements.', null, 1000000, 'Support informatique, Résolution de problèmes, Maintenance', 133, 'Assistance technique aux utilisateurs, Résolution des problèmes informatiques, Maintenance des équipements');
