INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Ingénieur en Science des Données', 'L’ingénieur en science des données conçoit des solutions pour collecter, traiter, et analyser des données massives, en vue d’extraire des informations pertinentes et de générer des modèles prédictifs.', null, 3800000, 'Conception de solutions en science des données, Collecte, Traitement, Analyse de données massives, Modèles prédictifs', 353, 'Conception de solutions pour collecter, traiter et analyser des données massives, Extraction d’informations pertinentes, Génération de modèles prédictifs'),
    ('Data Scientist', 'Le data scientist analyse des données variées, élabore des modèles statistiques et prédictifs, et propose des recommandations pour prendre des décisions éclairées au sein des entreprises.', null, 3600000, 'Analyse de données, Modèles statistiques, Modèles prédictifs, Recommandations', 353, 'Analyse de données variées, Élaboration de modèles statistiques et prédictifs, Proposition de recommandations pour des décisions éclairées'),
    ('Ingénieur en Intelligence Artificielle', 'L’ingénieur en intelligence artificielle développe des algorithmes et des modèles d’apprentissage automatique, en vue de créer des systèmes autonomes capables d’apprendre et de prendre des décisions.', null, 3800000, 'Développement d''algorithmes, Modèles d''apprentissage automatique, Systèmes autonomes', 353, 'Développement d’algorithmes, Création de modèles d’apprentissage automatique, Conception de systèmes autonomes'),
    ('Data Engineer', 'Le data engineer assure la gestion et la structuration des données au sein de l’entreprise, développe des systèmes d’acquisition et de stockage, et garantit la qualité des données.', null, 3500000, 'Gestion des données, Structuration, Développement de systèmes, Qualité des données', 353, 'Assurance de la gestion et de la structuration des données, Développement de systèmes d’acquisition et de stockage, Garantie de la qualité des données'),
    ('Chercheur en Intelligence Artificielle', 'Le chercheur en intelligence artificielle mène des recherches poussées pour innover dans le domaine, propose de nouvelles approches et techniques, et contribue à l’avancement de la discipline.', null, 4000000, 'Recherches en intelligence artificielle, Innovation, Nouvelles approches et techniques', 353, 'Mener des recherches poussées en intelligence artificielle, Proposition de nouvelles approches et techniques, Contribution à l’avancement de la discipline'),
    ('Analyste de Données', 'L’analyste de données exploite les données de l’entreprise pour en extraire des insights, réalise des rapports et des visualisations, et fournit des informations exploitables pour la prise de décisions.', null, 3200000, 'Exploitation de données, Rapports, Visualisations, Informations exploitables', 353, 'Exploitation des données de l’entreprise, Réalisation de rapports et de visualisations, Fourniture d’informations exploitables pour la prise de décisions'),
    ('Développeur en Intelligence Artificielle', 'Le développeur en intelligence artificielle crée des applications et des systèmes utilisant des technologies d’IA, en programmant et en optimisant les algorithmes pour répondre aux besoins spécifiques.', null, 3400000, 'Création d''applications en intelligence artificielle, Programmation, Optimisation d''algorithmes', 353, 'Création d’applications et de systèmes utilisant des technologies d’IA, Programmation et optimisation d’algorithmes'),
    ('Ingénieur en Traitement du Langage Naturel', 'L’ingénieur en traitement du langage naturel développe des solutions pour permettre aux machines de comprendre et de traiter le langage humain, contribuant ainsi au développement de chatbots et d’assistants virtuels.', null, 3600000, 'Développement de solutions en traitement du langage naturel, Compréhension du langage humain, Chatbots, Assistants virtuels', 353, 'Développement de solutions pour permettre aux machines de comprendre et de traiter le langage humain, Contribution au développement de chatbots et d’assistants virtuels'),
    ('Spécialiste en Apprentissage Automatique', 'Le spécialiste en apprentissage automatique conçoit des modèles d’apprentissage automatique pour résoudre des problèmes spécifiques, en utilisant des techniques telles que les réseaux de neurones et les arbres de décision.', null, 3400000, 'Conception de modèles d''apprentissage automatique, Réseaux de neurones, Arbres de décision', 353, 'Conception de modèles d’apprentissage automatique pour résoudre des problèmes spécifiques, Utilisation de techniques telles que les réseaux de neurones et les arbres de décision'),
    ('Architecte en Intelligence Artificielle', 'L’architecte en intelligence artificielle conçoit l’infrastructure des systèmes d’IA, choisit les technologies appropriées, et supervise la mise en place des solutions pour assurer une performance optimale.', null, 3800000, 'Conception d''infrastructure en intelligence artificielle, Choix de technologies, Supervision de la mise en place', 353, 'Conception de l’infrastructure des systèmes d’IA, Choix des technologies appropriées, Supervision de la mise en place pour assurer une performance optimale'),
    ('Analyste en Apprentissage Automatique', 'L’analyste en apprentissage automatique réalise des analyses approfondies des modèles existants, propose des améliorations, et assure la veille technologique pour rester à jour avec les avancées en matière d’IA.', null, 3300000, 'Analyses d''apprentissage automatique, Proposition d''améliorations, Veille technologique', 353, 'Réalisation d’analyses approfondies des modèles d’apprentissage automatique existants, Proposition d’améliorations, Veille technologique'),
    ('Ingénieur en Vision par Ordinateur', 'L’ingénieur en vision par ordinateur développe des systèmes permettant aux machines de comprendre et d’interpréter visuellement leur environnement, contribuant à des domaines tels que la reconnaissance d’objets et la réalité augmentée.', null, 3700000, 'Développement de systèmes en vision par ordinateur, Compréhension visuelle de l''environnement, Reconnaissance d''objets, Réalité augmentée', 353, 'Développement de systèmes permettant aux machines de comprendre et d’interpréter visuellement leur environnement, Contribution à des domaines tels que la reconnaissance d’objets et la réalité augmentée'),
    ('Consultant en Science des Données', 'Le consultant en science des données propose des solutions personnalisées aux entreprises, réalise des audits, et assure la formation des équipes pour maximiser l’exploitation des données.', null, 3500000, 'Proposition de solutions en science des données, Audits, Formation des équipes, Exploitation des données', 353, 'Proposition de solutions personnalisées aux entreprises, Réalisation d’audits, Formation des équipes pour maximiser l’exploitation des données'),
    ('Développeur de Chatbots', 'Le développeur de chatbots crée des applications conversationnelles automatisées, en utilisant des technologies d’IA pour permettre aux machines de comprendre et de répondre aux requêtes des utilisateurs.', null, 3300000, 'Création d''applications de chatbots, Technologies d''IA, Compréhension et réponse aux requêtes des utilisateurs', 353, 'Création d’applications conversationnelles automatisées en utilisant des technologies d’IA, Permettre aux machines de comprendre et de répondre aux requêtes des utilisateurs'),
    ('Ingénieur en Apprentissage Profond', 'L’ingénieur en apprentissage profond développe des modèles d’apprentissage profond, utilise des réseaux de neurones profonds pour des tâches complexes, et contribue aux avancées dans le domaine.', null, 3700000, 'Développement de modèles d''apprentissage profond, Réseaux de neurones profonds, Contributions aux avancées', 353, 'Développement de modèles d’apprentissage profond, Utilisation de réseaux de neurones profonds pour des tâches complexes, Contribution aux avancées dans le domaine');
