INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Archéologue', 'L’archéologue mène des fouilles, analyse les vestiges découverts et interprète les données pour comprendre et reconstituer l’histoire des civilisations anciennes et des sites archéologiques.', null, 3200000, 'Fouilles archéologiques, Analyse des vestiges, Interprétation des données, Histoire des civilisations anciennes, Sites archéologiques', 2, 'Mener des fouilles et interpréter les données'),
    ('Conservateur', 'Le conservateur est responsable de la gestion, de la préservation et de la valorisation des collections muséales et des sites patrimoniaux, en veillant à leur protection et à leur accessibilité au public.', null, 3500000, 'Gestion des collections, Préservation, Valorisation, Sites patrimoniaux, Protection, Accessibilité au public', 2, 'Gérer, préserver et valoriser les collections et les sites patrimoniaux'),
    ('Restaurateur', 'Le restaurateur intervient sur les œuvres d’art et les objets archéologiques pour les conserver, les restaurer et les protéger contre l’altération et le vieillissement, en respectant les techniques et les matériaux d’origine.', null, 3000000, 'Conservation, Restauration, Protection, Œuvres d’art, Objets archéologiques, Techniques de restauration, Matériaux d’origine', 2, 'Conserver et restaurer les œuvres d’art et les objets archéologiques'),
    ('Guide Conférencier', 'Le guide conférencier accompagne les visiteurs sur les sites archéologiques et dans les musées pour leur expliquer l’histoire, les contextes et les enjeux des œuvres et des vestiges présentés, en proposant des visites commentées.', null, 2500000, 'Accompagnement, Sites archéologiques, Musées, Explication, Histoire, Contexte, Enjeux, Visites commentées', 2, 'Accompagner et expliquer l’histoire des sites archéologiques et des musées'),
    ('Chargé de Documentation', 'Le chargé de documentation rassemble, classe et analyse les informations documentaires sur les sites archéologiques et les collections patrimoniales, en vue de constituer des bases de données et des outils de recherche.', null, 2800000, 'Gestion documentaire, Classement, Analyse, Sites archéologiques, Collections patrimoniales, Bases de données, Outils de recherche', 2, 'Rassembler et analyser les informations documentaires'),
    ('Expert en Archéologie Préventive', 'L’expert en archéologie préventive réalise des diagnostics et des études d’impact pour évaluer les conséquences des projets d’aménagement sur le patrimoine archéologique, en proposant des mesures de sauvegarde et de valorisation.', null, 3300000, 'Diagnostics, Études d’impact, Projets d’aménagement, Patrimoine archéologique, Mesures de sauvegarde, Mesures de valorisation', 2, 'Réaliser des diagnostics et des études d’impact'),
    ('Responsable de Site Archéologique', 'Le responsable de site archéologique assure la gestion et la valorisation d’un site archéologique, en organisant les fouilles, en assurant la conservation des vestiges et en animant des visites guidées.', null, 3100000, 'Gestion, Valorisation, Site archéologique, Organiser les fouilles, Conservation des vestiges, Animation des visites guidées', 2, 'Assurer la gestion et la valorisation d’un site archéologique'),
    ('Anthropologue', 'L’anthropologue étudie les sociétés humaines et les cultures à travers les âges, en analysant les pratiques sociales, les croyances et les modes de vie des populations passées et présentes.', null, 3400000, 'Étude des sociétés humaines, Étude des cultures, Pratiques sociales, Croyances, Modes de vie, Populations passées, Populations présentes', 2, 'Étudier les sociétés humaines et les cultures'),
    ('Archiviste', 'L’archiviste collecte, classe et conserve les documents d’archives et les documents patrimoniaux pour assurer leur accessibilité, leur préservation et leur transmission aux générations futures.', null, 2900000, 'Collecte, Classement, Conservation, Documents d’archives, Documents patrimoniaux, Accessibilité, Préservation, Transmission', 2, 'Collecter, classer et conserver les documents d’archives'),
    ('Agent de Médiation Culturelle', 'L’agent de médiation culturelle organise des activités pédagogiques et culturelles autour du patrimoine archéologique, en proposant des animations, des ateliers et des expositions pour sensibiliser le public.', null, 2700000, 'Médiation culturelle, Activités pédagogiques, Activités culturelles, Patrimoine archéologique, Animations, Ateliers, Expositions, Sensibilisation du public', 2, 'Organiser des activités pédagogiques et culturelles autour du patrimoine archéologique');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Artiste Peintre', 'L’artiste peintre crée des œuvres picturales en utilisant différentes techniques et matériaux pour exprimer sa créativité et transmettre des émotions ou des messages.', null, 2800000, 'Créativité, Techniques picturales, Matériaux, Expression artistique, Transmission d’émotions', 3, 'Créer des œuvres picturales pour exprimer sa créativité'),
    ('Sculpteur', 'Le sculpteur conçoit et réalise des sculptures en travaillant différentes matières telles que la pierre, le bois, le métal ou le plâtre, pour représenter des formes et des volumes.', null, 3000000, 'Conception, Réalisation, Sculptures, Matériaux, Formes, Volumes', 3, 'Concevoir et réaliser des sculptures'),
    ('Conservateur de Musée', 'Le conservateur de musée est chargé de la gestion, de la préservation et de la valorisation des collections artistiques et historiques d’un musée, en assurant leur conservation et leur exposition.', null, 3500000, 'Gestion des collections, Préservation, Valorisation, Musée, Conservation, Exposition', 3, 'Gérer, préserver et valoriser les collections d’un musée'),
    ('Restaurateur d’œuvres d’art', 'Le restaurateur d’œuvres d’art intervient sur les œuvres anciennes ou endommagées pour les conserver, les restaurer et les protéger contre l’altération et le vieillissement, en respectant les techniques et les matériaux d’origine.', null, 3200000, 'Conservation, Restauration, Protection, Œuvres d’art, Techniques de restauration, Matériaux d’origine', 3, 'Conserver et restaurer les œuvres d’art'),
    ('Galeriste', 'Le galeriste gère une galerie d’art, sélectionne les artistes et les œuvres à exposer, organise des expositions et des ventes, et assure la promotion et la commercialisation des œuvres.', null, 3000000, 'Gestion de galerie, Sélection des artistes, Sélection des œuvres, Expositions, Ventes, Promotion, Commercialisation', 3, 'Gérer une galerie d’art et promouvoir les œuvres'),
    ('Critique d’art', 'Le critique d’art analyse les œuvres artistiques, rédige des critiques et des articles pour les médias spécialisés, et participe à la diffusion et à la compréhension de l’art contemporain.', null, 2900000, 'Analyse des œuvres, Rédaction de critiques, Médias spécialisés, Diffusion de l’art contemporain', 3, 'Analyser et critiquer les œuvres artistiques'),
    ('Marchand d’art', 'Le marchand d’art achète, vend et négocie des œuvres d’art pour le compte de collectionneurs, d’investisseurs ou d’institutions, en assurant leur promotion et leur valorisation sur le marché.', null, 3500000, 'Achat, Vente, Négociation, Œuvres d’art, Promotion, Valorisation, Marché de l’art', 3, 'Acheter, vendre et négocier des œuvres d’art'),
    ('Illustrateur', 'L’illustrateur crée des images et des dessins pour accompagner des textes ou des concepts, en utilisant différentes techniques graphiques et artistiques pour communiquer visuellement.', null, 2700000, 'Création d’images, Création de dessins, Techniques graphiques, Techniques artistiques, Communication visuelle', 3, 'Créer des images et des dessins pour communiquer visuellement'),
    ('Archiviste d’Art', 'L’archiviste d’art collecte, classe et conserve les documents d’archives et les informations sur les artistes et les œuvres d’art pour assurer leur accessibilité et leur préservation.', null, 2600000, 'Collecte, Classement, Conservation, Documents d’archives, Informations, Accessibilité, Préservation', 3, 'Collecter, classer et conserver les informations sur les artistes et les œuvres d’art'),
    ('Professeur d’Arts Plastiques', 'Le professeur d’arts plastiques enseigne les techniques et les concepts artistiques aux élèves, encourage leur créativité et leur expression personnelle, et les initie à l’histoire de l’art.', null, 2800000, 'Enseignement, Techniques artistiques, Concepts artistiques, Créativité, Expression personnelle, Histoire de l’art', 3, 'Enseigner les techniques artistiques et l’histoire de l’art');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Acteur', 'L’acteur interprète des rôles dans des productions théâtrales, cinématographiques, télévisuelles ou audiovisuelles, en incarnant des personnages et en transmettant des émotions à un public.', null, 2500000, 'Interprétation, Rôles, Productions théâtrales, Productions cinématographiques, Productions télévisuelles, Productions audiovisuelles, Transmission d’émotions', 4, 'Interpréter des rôles et transmettre des émotions'),
    ('Réalisateur', 'Le réalisateur conçoit et dirige la mise en scène d’œuvres cinématographiques ou audiovisuelles, en supervisant l’ensemble du processus de création et en veillant à la cohérence artistique du projet.', null, 3000000, 'Conception, Mise en scène, Œuvres cinématographiques, Œuvres audiovisuelles, Processus de création, Cohérence artistique', 4, 'Concevoir et diriger la mise en scène d’œuvres cinématographiques'),
    ('Scénariste', 'Le scénariste écrit des scénarios et des dialogues pour des productions cinématographiques, télévisuelles, théâtrales ou audiovisuelles, en développant des histoires et des personnages captivants.', null, 2700000, 'Écriture de scénarios, Écriture de dialogues, Productions cinématographiques, Productions télévisuelles, Productions théâtrales, Productions audiovisuelles, Développement d’histoires, Développement de personnages', 4, 'Écrire des scénarios et des dialogues captivants'),
    ('Monteur', 'Le monteur assemble les différents éléments d’un film ou d’une production audiovisuelle (images, sons, musiques) pour créer une œuvre cohérente et rythmée, en suivant les indications du réalisateur.', null, 2600000, 'Montage, Assemblage, Film, Production audiovisuelle, Images, Sons, Musiques, Cohérence, Rythme', 4, 'Assembler les éléments d’un film pour créer une œuvre cohérente'),
    ('Directeur de la Photographie', 'Le directeur de la photographie conçoit et supervise la réalisation des prises de vue d’un film ou d’une production audiovisuelle, en choisissant les angles, les éclairages et les techniques visuelles pour créer une atmosphère et une esthétique spécifiques.', null, 2800000, 'Conception, Supervision, Prises de vue, Film, Production audiovisuelle, Angles, Éclairages, Techniques visuelles, Atmosphère, Esthétique', 4, 'Concevoir et superviser les prises de vue d’un film'),
    ('Producteur', 'Le producteur est chargé de coordonner et de financer la réalisation d’un film ou d’une production audiovisuelle, en assurant la gestion administrative, logistique et financière du projet.', null, 3200000, 'Coordination, Financement, Réalisation, Film, Production audiovisuelle, Gestion administrative, Gestion logistique, Gestion financière', 4, 'Coordonner et financer la réalisation d’un film'),
    ('Costumier', 'Le costumier conçoit et réalise les costumes des personnages d’un film, d’une pièce de théâtre ou d’une production audiovisuelle, en suivant les indications du réalisateur et en tenant compte de l’époque et du contexte.', null, 2400000, 'Conception, Réalisation, Costumes, Film, Pièce de théâtre, Production audiovisuelle, Indications du réalisateur, Époque, Contexte', 4, 'Concevoir et réaliser les costumes des personnages'),
    ('Casting Director', 'Le casting director sélectionne les acteurs et les figurants pour les rôles d’un film, d’une série télévisée ou d’une production théâtrale, en organisant des auditions et des essais pour trouver les meilleurs talents.', null, 2500000, 'Sélection, Acteurs, Figurants, Auditions, Essais, Film, Série télévisée, Production théâtrale, Talents', 4, 'Sélectionner les acteurs et les figurants pour les rôles'),
    ('Chef Décorateur', 'Le chef décorateur conçoit et réalise les décors et les environnements visuels d’un film, d’une pièce de théâtre ou d’une production audiovisuelle, en créant des atmosphères et des univers esthétiques.', null, 2700000, 'Conception, Réalisation, Décors, Environnements visuels, Film, Pièce de théâtre, Production audiovisuelle, Atmosphères, Univers esthétiques', 4, 'Concevoir et réaliser les décors d’un film'),
    ('Ingénieur du Son', 'L’ingénieur du son enregistre et mixe les dialogues, les bruits et les musiques d’un film ou d’une production audiovisuelle, en veillant à la qualité et à l’équilibre sonore de l’ensemble.', null, 2600000, 'Enregistrement, Mixage, Dialogues, Bruits, Musiques, Film, Production audiovisuelle, Qualité sonore, Équilibre sonore', 4, 'Enregistrer et mixer les éléments sonores d’un film');
