INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Chirurgien-dentiste', 'Le chirurgien-dentiste diagnostique et traite les affections dentaires et parodontales, réalise des actes de prophylaxie et de chirurgie buccale, et assure le suivi médical des patients pour maintenir leur santé bucco-dentaire.', null, 2500000, 'Chirurgie dentaire, Traitement, Prophylaxie, Parodontologie, Suivi médical, Santé bucco-dentaire', 36, 'Diagnostiquer et traiter les affections dentaires'),
    ('Orthodontiste', 'L’orthodontiste corrige les malpositions dentaires et les anomalies de la mâchoire en utilisant des appareils orthodontiques (bagues, arcs, etc.) et des techniques spécifiques, afin d’aligner les dents et d’améliorer l’occlusion dentaire.', null, 2600000, 'Orthodontie, Malpositions dentaires, Anomalies de la mâchoire, Appareils orthodontiques, Alignement des dents, Occlusion dentaire', 36, 'Corriger les malpositions dentaires'),
    ('Endodontiste', 'L’endodontiste traite les affections de la pulpe dentaire (nerf) et des tissus périapicaux (racine) en réalisant des traitements canalaire, des détartrages radiculaires et des chirurgies endodontiques pour préserver la dent et éviter son extraction.', null, 2700000, 'Endodontie, Pulpe dentaire, Tissus périapicaux, Traitements canalaire, Détartrages radiculaires, Chirurgies endodontiques', 36, 'Traiter les affections de la pulpe dentaire'),
    ('Parodontiste', 'Le parodontiste prend en charge les affections des tissus de soutien de la dent (gencive, os alvéolaire) en réalisant des soins parodontaux (détartrages, surfaçages radiculaires, greffes osseuses) pour maintenir la stabilité et la santé des gencives.', null, 2800000, 'Parodontologie, Tissus de soutien, Soins parodontaux, Détartrages, Surfaçages radiculaires, Greffes osseuses', 36, 'Prendre en charge les affections des tissus de soutien'),
    ('Implantologiste', 'L’implantologiste pose des implants dentaires pour remplacer des dents manquantes ou en mauvais état, en réalisant des greffes osseuses si nécessaire et en assurant le suivi post-opératoire pour garantir la réussite des interventions.', null, 2900000, 'Implantologie, Pose d’implants, Greffes osseuses, Suivi post-opératoire, Remplacement des dents, Réussite des interventions', 36, 'Poser des implants dentaires'),
    ('Pédiatre dentiste', 'Le pédiatre dentiste assure la prise en charge bucco-dentaire des enfants et des adolescents, en réalisant des soins préventifs, des traitements curatifs et des interventions spécifiques adaptées à leur jeune âge et à leurs besoins.', null, 3000000, 'Pédiatrie dentaire, Prise en charge, Enfants, Adolescents, Soins préventifs, Traitements curatifs', 36, 'Assurer la prise en charge bucco-dentaire des enfants'),
    ('Dentiste esthétique', 'Le dentiste esthétique réalise des actes de dentisterie esthétique pour améliorer l’apparence du sourire et de la dentition, en proposant des traitements de blanchiment, de pose de facettes ou de reconstitution dentaire.', null, 3100000, 'Dentisterie esthétique, Sourire, Dentition, Blanchiment, Facettes dentaires, Reconstitution dentaire', 36, 'Améliorer l’apparence du sourire et de la dentition'),
    ('Dentiste pédiatrique', 'Le dentiste pédiatrique s’occupe de la santé bucco-dentaire des enfants, en réalisant des soins adaptés à leur jeune âge, en les rassurant et en les accompagnant pour prévenir les pathologies dentaires et les phobies.', null, 3200000, 'Dentisterie pédiatrique, Santé bucco-dentaire, Soins adaptés, Prévention, Rassurer, Accompagner', 36, 'Prendre en charge la santé bucco-dentaire des enfants'),
    ('Prothésiste dentaire', 'Le prothésiste dentaire fabrique des prothèses dentaires (couronnes, bridges, prothèses amovibles, etc.) sur mesure en fonction des indications du chirurgien-dentiste, en utilisant des matériaux adaptés à chaque cas.', null, 3300000, 'Prothèses dentaires, Couronnes, Bridges, Prothèses amovibles, Fabrication sur mesure, Matériaux', 36, 'Fabriquer des prothèses dentaires sur mesure'),
    ('Assistant dentaire', 'L’assistant dentaire assiste le chirurgien-dentiste dans les interventions et les soins dentaires, en préparant le matériel, en accueillant les patients, en réalisant des radiographies et en assurant la gestion administrative du cabinet.', null, 3400000, 'Assistance dentaire, Soins dentaires, Préparation du matériel, Accueil des patients, Radiographies, Gestion administrative', 36, 'Assister le chirurgien-dentiste dans les interventions');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Médecin généraliste', 'Le médecin généraliste assure le suivi médical des patients, réalise des consultations pour établir des diagnostics, prescrit des traitements et oriente éventuellement vers des spécialistes en cas de besoin, en assurant une prise en charge globale et continue.', null, 2500000, 'Médecine générale, Suivi médical, Consultations, Diagnostics, Traitements, Prise en charge globale', 37, 'Assurer le suivi médical des patients'),
    ('Médecin urgentiste', 'Le médecin urgentiste prend en charge les patients en situation d’urgence médicale (traumatismes, maladies graves, etc.) en intervenant rapidement pour stabiliser leur état de santé, en coordonnant les équipes et en organisant les transferts vers les services spécialisés.', null, 2600000, 'Urgentiste, Prise en charge, Urgence médicale, Stabilisation, Coordination, Transferts', 37, 'Prendre en charge les patients en situation d’urgence'),
    ('Médecin de famille', 'Le médecin de famille assure le suivi médical des membres d’une même famille sur le long terme, en connaissant leur historique médical, en proposant des bilans de santé réguliers et en prodiguant des conseils pour prévenir les maladies et promouvoir la santé.', null, 2700000, 'Médecin de famille, Suivi médical, Historique médical, Bilans de santé, Prévention, Promotion de la santé', 37, 'Assurer le suivi médical des membres d’une même famille'),
    ('Médecin de campagne', 'Le médecin de campagne exerce en milieu rural ou semi-rural, en assurant des consultations médicales dans son cabinet ou à domicile, en intervenant sur des urgences et en participant à des actions de prévention et d’éducation à la santé au sein de la communauté.', null, 2800000, 'Médecin de campagne, Milieu rural, Consultations médicales, Domicile, Urgences, Prévention', 37, 'Assurer des consultations médicales en milieu rural'),
    ('Médecin coordinateur en Ehpad', 'Le médecin coordinateur en Ehpad assure la prise en charge médicale des résidents, coordonne les soins avec l’équipe pluridisciplinaire, réalise des évaluations régulières de l’état de santé des pensionnaires et assure le lien avec les médecins traitants.', null, 2900000, 'Ehpad, Prise en charge médicale, Coordination, Évaluations, Lien avec les médecins, Soins', 37, 'Assurer la prise en charge médicale des résidents en Ehpad'),
    ('Médecin du travail', 'Le médecin du travail veille à la santé des salariés au sein de l’entreprise, en réalisant des visites médicales périodiques, en conseillant sur les conditions de travail et en contribuant à la prévention des risques professionnels pour assurer la sécurité au travail.', null, 3000000, 'Médecin du travail, Santé des salariés, Visites médicales, Conditions de travail, Prévention des risques, Sécurité au travail', 37, 'Veiller à la santé des salariés au sein de l’entreprise'),
    ('Médecin humanitaire', 'Le médecin humanitaire intervient dans des zones de crise ou de conflit pour apporter des soins médicaux aux populations vulnérables, en travaillant avec des organisations humanitaires et en assurant le suivi des programmes de santé.', null, 3100000, 'Médecin humanitaire, Zones de crise, Soins médicaux, Populations vulnérables, Organisations humanitaires, Suivi des programmes', 37, 'Apporter des soins médicaux aux populations vulnérables'),
    ('Médecin gériatre', 'Le médecin gériatre prend en charge les personnes âgées en proposant des bilans de santé spécifiques, en évaluant leur autonomie et leur qualité de vie, en traitant les pathologies liées au vieillissement et en proposant un accompagnement adapté.', null, 3200000, 'Gériatrie, Personnes âgées, Bilans de santé, Autonomie, Qualité de vie, Traitement', 37, 'Prendre en charge les personnes âgées'),
    ('Médecin de santé publique', 'Le médecin de santé publique intervient au niveau des populations pour prévenir les maladies, améliorer les conditions sanitaires et promouvoir la santé collective, en mettant en place des politiques de santé et des actions de prévention.', null, 3300000, 'Santé publique, Prévention, Conditions sanitaires, Politiques de santé, Actions collectives, Population', 37, 'Intervenir au niveau des populations pour prévenir les maladies'),
    ('Médecin légiste', 'Le médecin légiste analyse les causes et les circonstances des décès, en réalisant des autopsies et des expertises médico-légales pour établir des rapports destinés à la justice, en contribuant à la résolution des affaires criminelles et des accidents.', null, 3400000, 'Légiste, Analyse des décès, Autopsies, Expertises médico-légales, Rapports, Justice', 37, 'Analyser les causes des décès et établir des rapports pour la justice');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Anatomiste pathologiste', 'L’anatomiste pathologiste analyse les échantillons biologiques (tissus, cellules, fluides) prélevés lors d’examens cliniques ou chirurgicaux, afin d’établir des diagnostics précis sur les maladies et les pathologies, en utilisant des techniques d’histologie, d’immunohistochimie et de biologie moléculaire.', null, 2500000, 'Anatomie pathologique, Analyse d’échantillons, Diagnostics, Histologie, Immunohistochimie, Biologie moléculaire', 39, 'Analyser les échantillons biologiques pour établir des diagnostics'),
    ('Technicien d’anatomie pathologique', 'Le technicien d’anatomie pathologique assiste le pathologiste dans la préparation des échantillons biologiques, en réalisant des coupes histologiques, des colorations spécifiques et des analyses morphologiques, pour permettre le diagnostic des maladies.', null, 2600000, 'Anatomie pathologique, Préparation des échantillons, Coupes histologiques, Colorations, Analyses morphologiques, Diagnostic', 39, 'Assister le pathologiste dans la préparation des échantillons'),
    ('Pathologiste moléculaire', 'Le pathologiste moléculaire étudie les altérations génétiques et moléculaires des cellules et des tissus pour comprendre les mécanismes des maladies et proposer des traitements ciblés, en utilisant des techniques de biologie moléculaire et de séquençage génétique.', null, 2700000, 'Pathologie moléculaire, Altérations génétiques, Biologie moléculaire, Séquençage génétique, Compréhension des maladies, Traitements ciblés', 39, 'Étudier les altérations génétiques et moléculaires des cellules'),
    ('Technicien de laboratoire en anatomie pathologique', 'Le technicien de laboratoire en anatomie pathologique réalise les analyses et les examens sur les échantillons biologiques, en respectant les protocoles et les normes de sécurité, pour fournir des résultats fiables aux pathologistes et aux médecins.', null, 2800000, 'Laboratoire, Anatomie pathologique, Analyses, Examens, Protocoles, Normes de sécurité', 39, 'Réaliser les analyses sur les échantillons biologiques'),
    ('Pathologiste cytopathologiste', 'Le pathologiste cytopathologiste étudie les cellules prélevées par frottis (cytologie) pour détecter d’éventuelles anomalies ou signes de cancer, en réalisant des examens au microscope et en interprétant les résultats pour établir un diagnostic.', null, 2900000, 'Cytopathologie, Frottis, Cytologie, Anomalies cellulaires, Cancer, Diagnostic', 39, 'Étudier les cellules prélevées par frottis pour détecter d’éventuelles anomalies'),
    ('Biologiste médical', 'Le biologiste médical réalise des analyses biologiques sur des échantillons humains (sang, urine, tissus, etc.) pour établir des diagnostics, surveiller l’évolution de maladies ou évaluer l’efficacité des traitements, en utilisant des techniques de biologie cellulaire et moléculaire.', null, 3000000, 'Biologie médicale, Analyses biologiques, Diagnostics, Surveillance, Évaluation des traitements, Techniques', 39, 'Réaliser des analyses biologiques sur des échantillons humains'),
    ('Pathologiste neuropathologiste', 'Le pathologiste neuropathologiste étudie les tissus nerveux et les lésions du système nerveux central et périphérique pour diagnostiquer les maladies neurologiques (tumeurs cérébrales, maladies dégénératives, etc.) et guider les traitements médicaux ou chirurgicaux.', null, 3100000, 'Neuropathologie, Tissus nerveux, Lésions, Maladies neurologiques, Diagnostics, Traitements', 39, 'Étudier les tissus nerveux pour diagnostiquer les maladies neurologiques'),
    ('Pathologiste pédiatrique', 'Le pathologiste pédiatrique étudie les maladies et les pathologies spécifiques aux enfants et aux nourrissons, en réalisant des analyses sur les tissus et les fluides biologiques pour établir des diagnostics précis et proposer des traitements adaptés à leur jeune âge.', null, 3200000, 'Pédiatrie, Maladies pédiatriques, Diagnostics, Traitements, Analyses, Tissus biologiques', 39, 'Étudier les maladies spécifiques aux enfants'),
    ('Pathologiste oncologue', 'Le pathologiste oncologue analyse les tumeurs et les cancers à partir des échantillons biologiques prélevés chez les patients, en identifiant les caractéristiques tumorales et en proposant des classifications et des stratégies thérapeutiques adaptées à chaque cas.', null, 3300000, 'Oncologie, Tumeurs, Cancers, Caractéristiques tumorales, Classifications, Stratégies thérapeutiques', 39, 'Analyser les tumeurs et les cancers'),
    ('Pathologiste dermatopathologiste', 'Le pathologiste dermatopathologiste étudie les affections de la peau et des tissus cutanés pour établir des diagnostics précis sur les maladies dermatologiques (cancers de la peau, infections, allergies, etc.) et guider les traitements dermatologiques.', null, 3400000, 'Dermatopathologie, Affections de la peau, Diagnostics dermatologiques, Traitements, Tissus cutanés, Maladies dermatologiques', 39, 'Étudier les affections de la peau pour établir des diagnostics précis');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Anesthésiste-réanimateur', 'L’anesthésiste-réanimateur assure la prise en charge anesthésique des patients avant, pendant et après une intervention chirurgicale, en choisissant les techniques et les médicaments adaptés, en surveillant leur état de santé et en anticipant les complications éventuelles.', null, 2500000, 'Anesthésie, Réanimation, Prise en charge, Chirurgie, Surveillance médicale, Complications', 40, 'Assurer la prise en charge anesthésique des patients'),
    ('Infirmier anesthésiste', 'L’infirmier anesthésiste assiste l’anesthésiste-réanimateur dans la préparation et la mise en œuvre des anesthésies, en assurant le suivi des patients avant, pendant et après l’intervention chirurgicale, en collaboration avec l’équipe médicale.', null, 2600000, 'Anesthésie, Préparation, Suivi des patients, Intervention chirurgicale, Collaboration, Équipe médicale', 40, 'Assister l’anesthésiste-réanimateur dans la mise en œuvre des anesthésies'),
    ('Réanimateur médical', 'Le réanimateur médical prend en charge les patients en situation de détresse vitale (traumatismes, infections graves, insuffisances d’organes, etc.) en leur assurant une surveillance continue et en mettant en œuvre des traitements adaptés pour stabiliser leur état de santé.', null, 2700000, 'Réanimation, Détresse vitale, Surveillance médicale, Traitements, Stabilisation, État de santé', 40, 'Prendre en charge les patients en situation de détresse vitale'),
    ('Médecin réanimateur', 'Le médecin réanimateur supervise l’équipe de réanimation, établit les protocoles de prise en charge des patients, interprète les examens et les bilans biologiques, et coordonne les soins en réanimation pour assurer une prise en charge optimale.', null, 2800000, 'Réanimation, Supervision, Protocoles, Interprétation des examens, Coordination des soins, Prise en charge', 40, 'Superviser l’équipe de réanimation et coordonner les soins'),
    ('Infirmier de réanimation', 'L’infirmier de réanimation assure les soins intensifs aux patients en état critique, en réalisant les gestes techniques (monitorage, intubation, perfusion, etc.), en surveillant les paramètres vitaux et en assurant le suivi des traitements prescrits par l’équipe médicale.', null, 2900000, 'Réanimation, Soins intensifs, Gestes techniques, Surveillance, Suivi des traitements, Équipe médicale', 40, 'Assurer les soins intensifs aux patients en état critique'),
    ('Anesthésiste pédiatrique', 'L’anesthésiste pédiatrique assure la prise en charge anesthésique des enfants et des nourrissons lors d’interventions chirurgicales, en adaptant les techniques et les doses de médicaments aux spécificités de leur organisme, en collaboration avec l’équipe médicale.', null, 3000000, 'Anesthésie pédiatrique, Enfants, Nourrissons, Chirurgie, Adaptation des doses, Collaboration', 40, 'Assurer la prise en charge anesthésique des enfants'),
    ('Médecin anesthésiste', 'Le médecin anesthésiste réalise les consultations pré-anesthésiques, assure l’anesthésie des patients pour des actes chirurgicaux ou des examens médicaux, et assure leur surveillance post-opératoire pour prévenir les complications.', null, 3100000, 'Anesthésie, Consultations pré-anesthésiques, Surveillance post-opératoire, Prévention des complications, Actes chirurgicaux, Examens médicaux', 40, 'Réaliser les consultations pré-anesthésiques et l’anesthésie'),
    ('Infirmier de bloc opératoire', 'L’infirmier de bloc opératoire assiste l’équipe chirurgicale dans la préparation et la réalisation des interventions, en assurant la sécurité du patient, la stérilisation du matériel et la traçabilité des actes réalisés pendant l’opération.', null, 3200000, 'Bloc opératoire, Chirurgie, Sécurité du patient, Stérilisation, Traçabilité, Intervention', 40, 'Assister l’équipe chirurgicale dans la réalisation des interventions'),
    ('Anesthésiste-réanimateur pédiatrique', 'L’anesthésiste-réanimateur pédiatrique assure la prise en charge anesthésique des enfants et des nourrissons lors d’interventions chirurgicales ou d’examens médicaux, en utilisant des techniques adaptées à leur jeune âge et en assurant leur surveillance post-opératoire.', null, 3300000, 'Anesthésie pédiatrique, Chirurgie, Examens médicaux, Techniques adaptées, Surveillance post-opératoire, Enfants', 40, 'Assurer la prise en charge anesthésique des enfants'),
    ('Anesthésiste obstétrical', 'L’anesthésiste obstétrical assure l’analgésie des femmes enceintes lors de l’accouchement par voie basse ou par césarienne, en utilisant des techniques d’anesthésie locorégionale ou générale, et en assurant la surveillance mère-enfant pendant et après l’intervention.', null, 3400000, 'Analgésie obstétricale, Accouchement, Techniques d’anesthésie, Surveillance mère-enfant, Voie basse, Césarienne', 40, 'Assurer l’analgésie des femmes enceintes lors de l’accouchement');
