INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Médecin interniste', 'Le médecin interniste diagnostique et prend en charge les maladies complexes et les pathologies multiples qui affectent plusieurs organes, en réalisant des examens cliniques approfondis, en interprétant les résultats d’examens complémentaires et en prescrivant des traitements adaptés.', null, 4000000, 'Médecine interne, Diagnostic, Prise en charge, Maladies complexes, Examens cliniques, Traitements', 41, 'Diagnostiquer et prendre en charge les maladies complexes'),
    ('Cardiologue', 'Le cardiologue est spécialisé dans le diagnostic et le traitement des maladies du cœur et des vaisseaux sanguins, en réalisant des examens cardiologiques (électrocardiogramme, échographie cardiaque, etc.), en interprétant les résultats et en proposant des interventions thérapeutiques.', null, 4200000, 'Cardiologie, Diagnostic, Traitement, Maladies cardiovasculaires, Examens cardiologiques, Interventions thérapeutiques', 41, 'Diagnostiquer et traiter les maladies du cœur'),
    ('Gastro-entérologue', 'Le gastro-entérologue est spécialisé dans le diagnostic et le traitement des maladies de l’appareil digestif (estomac, intestins, foie, etc.), en réalisant des endoscopies, des échographies et des analyses biologiques, et en proposant des traitements médicamenteux ou chirurgicaux.', null, 4400000, 'Gastro-entérologie, Diagnostic, Traitement, Maladies digestives, Endoscopies, Traitements médicamenteux', 41, 'Diagnostiquer et traiter les maladies de l’appareil digestif'),
    ('Pneumologue', 'Le pneumologue est spécialisé dans le diagnostic et le traitement des maladies respiratoires (asthme, bronchites, pneumonies, etc.), en réalisant des explorations fonctionnelles respiratoires, des radiographies pulmonaires et des analyses de gaz du sang, et en proposant des traitements adaptés.', null, 4600000, 'Pneumologie, Diagnostic, Traitement, Maladies respiratoires, Explorations fonctionnelles, Radiographies pulmonaires', 41, 'Diagnostiquer et traiter les maladies respiratoires'),
    ('Rhumatologue', 'Le rhumatologue est spécialisé dans le diagnostic et le traitement des maladies articulaires, osseuses et musculaires (arthrite, ostéoporose, lombalgie, etc.), en réalisant des examens cliniques, des radiographies et des échographies, et en proposant des traitements médicamenteux ou des rééducations fonctionnelles.', null, 4800000, 'Rhumatologie, Diagnostic, Traitement, Maladies articulaires, Radiographies, Traitements médicamenteux', 41, 'Diagnostiquer et traiter les maladies articulaires'),
    ('Néphrologue', 'Le néphrologue est spécialisé dans le diagnostic et le traitement des maladies rénales (insuffisance rénale, lithiases, glomérulonéphrites, etc.), en réalisant des bilans biologiques, des échographies rénales et des biopsies, et en proposant des traitements médicamenteux ou des dialyses.', null, 5000000, 'Néphrologie, Diagnostic, Traitement, Maladies rénales, Bilans biologiques, Dialyses', 41, 'Diagnostiquer et traiter les maladies rénales'),
    ('Endocrinologue', 'L’endocrinologue est spécialisé dans le diagnostic et le traitement des maladies hormonales (diabète, hypothyroïdie, hyperthyroïdie, etc.), en réalisant des dosages hormonaux, des échographies thyroïdiennes et des biopsies, et en proposant des traitements médicamenteux ou des chirurgies.', null, 5200000, 'Endocrinologie, Diagnostic, Traitement, Maladies hormonales, Dosages hormonaux, Chirurgies', 41, 'Diagnostiquer et traiter les maladies hormonales'),
    ('Hématologue', 'L’hématologue est spécialisé dans le diagnostic et le traitement des maladies du sang (leucémies, anémies, hémophilie, etc.), en réalisant des analyses cytologiques, des biopsies de moelle osseuse et des transfusions sanguines, et en proposant des traitements médicamenteux ou des greffes de moelle.', null, 5400000, 'Hématologie, Diagnostic, Traitement, Maladies du sang, Analyses cytologiques, Transfusions sanguines', 41, 'Diagnostiquer et traiter les maladies du sang'),
    ('Oncologue médical', 'L’oncologue médical est spécialisé dans le diagnostic et le traitement des cancers (sein, poumon, colon, etc.), en réalisant des bilans d’extension, des chimiothérapies, des immunothérapies et des hormonothérapies, et en assurant le suivi des patients pendant et après les traitements.', null, 5600000, 'Oncologie médicale, Diagnostic, Traitement, Cancers, Bilans d’extension, Chimiothérapies', 41, 'Diagnostiquer et traiter les cancers'),
    ('Neurologue', 'Le neurologue est spécialisé dans le diagnostic et le traitement des maladies du système nerveux (épilepsie, AVC, maladie de Parkinson, etc.), en réalisant des examens neurologiques, des électroencéphalogrammes et des IRM cérébrales, et en proposant des traitements médicamenteux ou des rééducations.', null, 5800000, 'Neurologie, Diagnostic, Traitement, Maladies du système nerveux, Examens neurologiques, IRM cérébrales', 41, 'Diagnostiquer et traiter les maladies du système nerveux');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Gynécologue-obstétricien', 'Le gynécologue-obstétricien est spécialisé dans la prise en charge médicale des femmes enceintes et des pathologies gynécologiques, en réalisant des consultations, des examens cliniques, des échographies obstétricales et des accouchements.', null, 6000000, 'Gynécologie-obstétrique, Consultations, Examens cliniques, Echographies obstétricales, Accouchements, Prise en charge médicale', 42, 'Assurer la prise en charge médicale des femmes enceintes'),
    ('Sage-femme', 'La sage-femme assure le suivi médical de la grossesse, de l’accouchement et du post-partum, en réalisant des consultations prénatales, des surveillances du travail, des accouchements physiologiques et des visites postnatales, et en dispensant des conseils à la future maman.', null, 2500000, 'Suivi de grossesse, Accouchement, Post-partum, Consultations prénatales, Surveillance du travail, Visites postnatales', 42, 'Assurer le suivi médical de la grossesse'),
    ('Obstétricien', 'L’obstétricien est spécialisé dans la prise en charge médicale et chirurgicale des femmes enceintes et des accouchements, en réalisant des examens obstétricaux, des monitoring fœtaux, des césariennes et des réanimations néonatales.', null, 5500000, 'Obstétrique, Prise en charge médicale, Césariennes, Monitoring fœtaux, Réanimations néonatales, Accouchements', 42, 'Assurer la prise en charge des femmes enceintes'),
    ('Gynécologue médical', 'Le gynécologue médical est spécialisé dans le diagnostic et le traitement des pathologies gynécologiques non chirurgicales, en réalisant des consultations, des frottis cervicaux, des échographies pelviennes et des colposcopies, et en prescrivant des traitements médicamenteux.', null, 4500000, 'Gynécologie médicale, Diagnostic, Traitement, Consultations, Frottis cervicaux, Echographies pelviennes', 42, 'Diagnostiquer et traiter les pathologies gynécologiques'),
    ('Gynécologue chirurgical', 'Le gynécologue chirurgical est spécialisé dans le diagnostic et le traitement des pathologies gynécologiques nécessitant une intervention chirurgicale, en réalisant des interventions telles que les hystérectomies, les salpingectomies et les réparations de prolapsus.', null, 5000000, 'Gynécologie chirurgicale, Diagnostic, Traitement, Hystérectomies, Salpingectomies, Réparations de prolapsus', 42, 'Diagnostiquer et traiter les pathologies gynécologiques chirurgicales'),
    ('Pédiatre néonatalogiste', 'Le pédiatre néonatalogiste est spécialisé dans la prise en charge médicale des nouveau-nés prématurés ou présentant des pathologies néonatales, en réalisant des examens cliniques, des réanimations néonatales et des suivis de développement.', null, 5700000, 'Pédiatrie néonatale, Prise en charge médicale, Réanimations néonatales, Suivis de développement, Nouveau-nés prématurés', 42, 'Assurer la prise en charge des nouveau-nés'),
    ('Femme-médecin', 'La femme-médecin est spécialisée dans la médecine générale et la gynécologie médicale, en assurant le suivi médical des femmes, des adolescents et des enfants, en réalisant des consultations et des examens gynécologiques.', null, 3500000, 'Médecine générale, Gynécologie médicale, Suivi médical, Consultations, Examens gynécologiques, Adolescents', 42, 'Assurer le suivi médical des femmes'),
    ('Infirmier obstétricien', 'L’infirmier obstétricien assiste les professionnels de santé lors des accouchements, en réalisant des surveillances du travail, des prélèvements, des actes de prévention et des soins aux nouveau-nés et aux mères.', null, 1800000, 'Surveillance du travail, Prélèvements, Soins aux nouveau-nés, Soins aux mères, Accouchements, Assistance médicale', 42, 'Assister les professionnels de santé lors des accouchements'),
    ('Échographiste obstétrical', 'L’échographiste obstétrical réalise des échographies de grossesse pour suivre le développement du fœtus et dépister d’éventuelles anomalies, en interprétant les images échographiques et en établissant des rapports pour les médecins.', null, 2200000, 'Echographie obstétricale, Suivi de grossesse, Dépistage d’anomalies, Interprétation des images, Etablissement de rapports, Médecins', 42, 'Réaliser des échographies de grossesse'),
    ('Psychologue périnatal', 'Le psychologue périnatal accompagne les femmes enceintes et les jeunes parents dans leur vécu émotionnel autour de la grossesse, de l’accouchement et de la parentalité, en réalisant des entretiens individuels et des groupes de parole.', null, 2300000, 'Psychologie périnatale, Accompagnement émotionnel, Grossesse, Accouchement, Parentalité, Entretiens individuels', 42, 'Accompagner les femmes enceintes et les jeunes parents');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Pharmacien galéniste', 'Le pharmacien galéniste est spécialisé dans la formulation, la fabrication et le contrôle de qualité des médicaments, en utilisant des techniques de pharmacie galénique pour produire des formes pharmaceutiques solides, liquides ou semi-solides.', null, 4000000, 'Formulation, Fabrication, Contrôle de qualité, Médicaments, Pharmacologie, Techniques de pharmacie galénique', 43, 'Formuler, fabriquer et contrôler la qualité des médicaments'),
    ('Pharmacologue', 'Le pharmacologue étudie les propriétés pharmacologiques des médicaments, en réalisant des recherches sur leur mode d’action, leur toxicité et leurs effets thérapeutiques, afin de contribuer au développement de nouveaux médicaments.', null, 4500000, 'Propriétés pharmacologiques, Recherches, Mode d’action, Toxicité, Effets thérapeutiques, Développement de médicaments', 43, 'Etudier les propriétés pharmacologiques des médicaments'),
    ('Pharmacien réglementaire', 'Le pharmacien réglementaire assure la conformité des médicaments aux normes réglementaires en vigueur, en réalisant des analyses documentaires, des études de stabilité et des dossiers d’autorisation de mise sur le marché.', null, 5000000, 'Conformité réglementaire, Normes réglementaires, Analyses documentaires, Etudes de stabilité, Autorisation de mise sur le marché, Dossiers réglementaires', 43, 'Assurer la conformité des médicaments aux normes réglementaires'),
    ('Responsable qualité pharmaceutique', 'Le responsable qualité pharmaceutique supervise la mise en place et le suivi du système qualité dans les laboratoires pharmaceutiques, en assurant le respect des bonnes pratiques de fabrication et des normes ISO.', null, 5500000, 'Qualité pharmaceutique, Système qualité, Bonnes pratiques de fabrication, Normes ISO, Supervision, Suivi', 43, 'Superviser la mise en place du système qualité'),
    ('Pharmacien toxicologue', 'Le pharmacien toxicologue étudie les effets toxiques des médicaments sur l’organisme humain, en réalisant des évaluations des risques toxicologiques et des analyses de sécurité des produits pharmaceutiques.', null, 4800000, 'Effets toxiques, Evaluations des risques, Analyses de sécurité, Médicaments, Toxicologie, Organisme humain', 43, 'Etudier les effets toxiques des médicaments'),
    ('Pharmacien hospitalier', 'Le pharmacien hospitalier assure la gestion des médicaments et la dispensation des traitements aux patients hospitalisés, en réalisant des préparations magistrales, des conseils thérapeutiques et des contrôles pharmaceutiques.', null, 3800000, 'Gestion des médicaments, Dispensation des traitements, Préparations magistrales, Conseils thérapeutiques, Contrôles pharmaceutiques, Patients hospitalisés', 43, 'Assurer la gestion des médicaments à l’hôpital'),
    ('Pharmacien industriel', 'Le pharmacien industriel travaille dans l’industrie pharmaceutique pour développer, produire et commercialiser des médicaments, en assurant le suivi des processus de fabrication et des contrôles qualité.', null, 6000000, 'Industrie pharmaceutique, Développement, Production, Commercialisation, Suivi des processus, Contrôles qualité', 43, 'Développer, produire et commercialiser des médicaments'),
    ('Pharmacien d’officine', 'Le pharmacien d’officine exerce en pharmacie d’officine pour délivrer des médicaments sur ordonnance et des conseils pharmaceutiques aux patients, en assurant le suivi de leur traitement et la gestion des stocks de médicaments.', null, 3500000, 'Pharmacie d’officine, Délivrance de médicaments, Conseils pharmaceutiques, Suivi de traitement, Gestion des stocks, Patients', 43, 'Délivrer des médicaments et des conseils pharmaceutiques'),
    ('Pharmacien chercheur', 'Le pharmacien chercheur mène des travaux de recherche dans le domaine pharmaceutique pour découvrir de nouveaux médicaments, en réalisant des études précliniques, des essais cliniques et des analyses de données.', null, 5500000, 'Recherche pharmaceutique, Découverte de médicaments, Etudes précliniques, Essais cliniques, Analyses de données, Chercheur', 43, 'Mener des travaux de recherche pharmaceutique'),
    ('Pharmacien biologiste', 'Le pharmacien biologiste réalise des analyses biologiques et des examens de laboratoire pour contribuer au diagnostic et au suivi des patients, en utilisant des techniques de biologie médicale et des équipements de laboratoire.', null, 4200000, 'Analyses biologiques, Examens de laboratoire, Diagnostic, Suivi des patients, Biologie médicale, Equipements de laboratoire', 43, 'Contribuer au diagnostic et au suivi des patients');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Pharmacien', 'Le pharmacien est un professionnel de santé spécialisé dans la préparation, la dispensation et le conseil sur les médicaments, en assurant la gestion de l’officine, la délivrance des ordonnances et l’accompagnement des patients.', null, 3500000, 'Préparation, Dispensation, Conseil, Gestion de l’officine, Délivrance des ordonnances, Accompagnement des patients', 44, 'Assurer la gestion de l’officine et la délivrance des ordonnances'),
    ('Chimiste pharmaceutique', 'Le chimiste pharmaceutique est spécialisé dans la conception, la fabrication et le contrôle qualité des médicaments, en réalisant des analyses chimiques, des tests de stabilité et des études de formulation.', null, 4500000, 'Conception, Fabrication, Contrôle qualité, Analyses chimiques, Tests de stabilité, Etudes de formulation', 44, 'Concevoir, fabriquer et contrôler la qualité des médicaments'),
    ('Pharmacologue', 'Le pharmacologue est spécialisé dans l’étude des effets des médicaments sur l’organisme, en réalisant des tests pharmacologiques, des études de toxicologie et des essais cliniques, pour garantir leur efficacité et leur sécurité.', null, 5000000, 'Tests pharmacologiques, Etudes de toxicologie, Essais cliniques, Efficacité des médicaments, Sécurité des médicaments, Effets sur l’organisme', 44, 'Etudier les effets des médicaments sur l’organisme'),
    ('Pharmacien hospitalier', 'Le pharmacien hospitalier est spécialisé dans la gestion et la dispensation des médicaments en milieu hospitalier, en assurant la préparation des doses, la validation des prescriptions et le suivi pharmaceutique des patients.', null, 3800000, 'Gestion, Dispensation, Préparation des doses, Validation des prescriptions, Suivi pharmaceutique, Milieu hospitalier', 44, 'Assurer la gestion et la dispensation des médicaments en milieu hospitalier'),
    ('Pharmacien industriel', 'Le pharmacien industriel est spécialisé dans la fabrication et le contrôle qualité des médicaments en industrie pharmaceutique, en supervisant les procédés de production, les contrôles réglementaires et les normes de qualité.', null, 4800000, 'Fabrication, Contrôle qualité, Industrie pharmaceutique, Procédés de production, Contrôles réglementaires, Normes de qualité', 44, 'Superviser la fabrication et le contrôle qualité des médicaments'),
    ('Pharmacien biologiste', 'Le pharmacien biologiste est spécialisé dans l’analyse et l’interprétation des examens biologiques, en réalisant des analyses médicales, des diagnostics et des conseils thérapeutiques, pour contribuer au suivi médical des patients.', null, 4200000, 'Analyse, Interprétation, Examens biologiques, Diagnostics, Conseils thérapeutiques, Suivi médical', 44, 'Contribuer au suivi médical des patients'),
    ('Ingénieur chimiste', 'L’ingénieur chimiste est spécialisé dans la conception et la mise en œuvre des procédés de fabrication pharmaceutique, en développant de nouvelles formulations, en optimisant les techniques de production et en assurant le respect des normes de qualité.', null, 5500000, 'Conception, Mise en œuvre, Procédés de fabrication, Nouvelles formulations, Techniques de production, Normes de qualité', 44, 'Concevoir et mettre en œuvre les procédés de fabrication pharmaceutique'),
    ('Technicien de laboratoire pharmaceutique', 'Le technicien de laboratoire pharmaceutique est spécialisé dans la réalisation des analyses et des contrôles qualité des matières premières et des produits finis, en utilisant des techniques analytiques et des équipements spécialisés.', null, 2800000, 'Analyses, Contrôles qualité, Matières premières, Produits finis, Techniques analytiques, Equipements spécialisés', 44, 'Réaliser les analyses et les contrôles qualité des produits pharmaceutiques'),
    ('Responsable assurance qualité', 'Le responsable assurance qualité est spécialisé dans la mise en place et le suivi des procédures qualité en industrie pharmaceutique, en assurant la conformité des produits aux normes réglementaires et en réalisant des audits internes.', null, 6000000, 'Assurance qualité, Procédures qualité, Conformité réglementaire, Audits internes, Industrie pharmaceutique, Suivi des normes', 44, 'Mettre en place et suivre les procédures qualité en industrie pharmaceutique'),
    ('Délégué pharmaceutique', 'Le délégué pharmaceutique est spécialisé dans la promotion et la vente des produits pharmaceutiques auprès des professionnels de santé, en assurant la visite médicale, la formation continue et la gestion des relations clients.', null, 3200000, 'Promotion, Vente, Produits pharmaceutiques, Visite médicale, Formation continue, Relations clients', 44, 'Promouvoir et vendre les produits pharmaceutiques');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Oto-Rhino-Laryngologiste', 'L’oto-rhino-laryngologiste est un médecin spécialisé dans le diagnostic et le traitement des maladies de l’oreille, du nez et de la gorge, en réalisant des examens cliniques, des interventions chirurgicales et des prescriptions médicales.', null, 7000000, 'Diagnostic, Traitement, Maladies de l’oreille, du nez et de la gorge, Examens cliniques, Interventions chirurgicales, Prescriptions médicales', 45, 'Diagnostiquer et traiter les maladies de l’oreille, du nez et de la gorge'),
    ('Audiologiste', 'L’audiologiste est un professionnel de santé spécialisé dans l’évaluation et la rééducation des troubles de l’audition, en réalisant des tests auditifs, des ajustements d’appareils auditifs et des séances de rééducation.', null, 6000000, 'Evaluation, Rééducation, Troubles de l’audition, Tests auditifs, Appareils auditifs, Séances de rééducation', 45, 'Evaluer et rééduquer les troubles de l’audition'),
    ('Chirurgien ORL', 'Le chirurgien ORL est un médecin spécialisé dans la réalisation d’interventions chirurgicales au niveau de l’oreille, du nez et de la gorge, en traitant les pathologies comme les sinusites, les polypes nasaux ou les amygdales.', null, 8000000, 'Interventions chirurgicales, Oreille, Nez, Gorge, Sinusites, Polypes nasaux, Amygdales', 45, 'Réaliser des interventions chirurgicales au niveau de l’oreille, du nez et de la gorge'),
    ('Phoniatre', 'Le phoniatre est un médecin spécialisé dans la rééducation des troubles de la voix, de la parole et de la déglutition, en réalisant des bilans vocaux, des séances de rééducation et des suivis thérapeutiques.', null, 6500000, 'Rééducation, Troubles de la voix, de la parole, de la déglutition, Bilans vocaux, Séances de rééducation, Suivis thérapeutiques', 45, 'Rééduquer les troubles de la voix, de la parole et de la déglutition'),
    ('ORL pédiatrique', 'L’ORL pédiatrique est un médecin spécialisé dans le diagnostic et le traitement des pathologies ORL chez les enfants, en réalisant des consultations pédiatriques, des examens approfondis et des suivis thérapeutiques.', null, 7500000, 'Diagnostic, Traitement, Pathologies ORL, Enfants, Consultations pédiatriques, Examens approfondis, Suivis thérapeutiques', 45, 'Diagnostiquer et traiter les pathologies ORL chez les enfants'),
    ('Audioprothésiste', 'L’audioprothésiste est un professionnel de santé spécialisé dans l’appareillage auditif, en réalisant des bilans auditifs, des ajustements d’appareils et des conseils personnalisés pour les patients souffrant de troubles de l’audition.', null, 5000000, 'Appareillage auditif, Bilans auditifs, Ajustements d’appareils, Conseils personnalisés, Troubles de l’audition, Patients', 45, 'Réaliser l’appareillage auditif pour les patients souffrant de troubles de l’audition'),
    ('Technicien ORL', 'Le technicien ORL est spécialisé dans la réalisation des examens médicaux et des tests diagnostiques en ORL, en assistant les médecins lors des consultations, des explorations fonctionnelles et des interventions chirurgicales.', null, 3500000, 'Examens médicaux, Tests diagnostiques, ORL, Consultations, Explorations fonctionnelles, Interventions chirurgicales', 45, 'Réaliser des examens médicaux et des tests diagnostiques en ORL'),
    ('Orthophoniste', 'L’orthophoniste est un professionnel de santé spécialisé dans la rééducation des troubles du langage, de la parole et de la déglutition, en réalisant des bilans orthophoniques, des séances de rééducation et des suivis thérapeutiques.', null, 5500000, 'Rééducation, Troubles du langage, de la parole, de la déglutition, Bilans orthophoniques, Séances de rééducation, Suivis thérapeutiques', 45, 'Rééduquer les troubles du langage, de la parole et de la déglutition'),
    ('Assistant audioprothésiste', 'L’assistant audioprothésiste est spécialisé dans l’accompagnement du patient pour l’appareillage auditif, en réalisant des prises d’empreintes, des réglages d’appareils et des conseils d’utilisation.', null, 3000000, 'Appareillage auditif, Prises d’empreintes, Réglages d’appareils, Conseils d’utilisation, Accompagnement du patient', 45, 'Assister le patient pour l’appareillage auditif'),
    ('Assistant ORL', 'L’assistant ORL est spécialisé dans l’assistance du médecin lors des consultations et des interventions chirurgicales en ORL, en préparant le matériel médical, en assistant pendant les actes médicaux et en assurant le suivi post-opératoire.', null, 3200000, 'Assistance, Consultations, Interventions chirurgicales, ORL, Préparation du matériel médical, Suivi post-opératoire', 45, 'Assister le médecin lors des consultations et des interventions chirurgicales en ORL');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Radiologue', 'Le radiologue est un médecin spécialisé dans l’interprétation des images médicales, en réalisant des examens radiologiques comme les radiographies, les échographies et les scanners, et en fournissant des diagnostics précis aux autres médecins.', null, 9000000, 'Interprétation, Images médicales, Examens radiologiques, Radiographies, Echographies, Scanners, Diagnostics', 46, 'Interpréter les images médicales et fournir des diagnostics précis'),
    ('Manipulateur en radiologie', 'Le manipulateur en radiologie est un professionnel de santé spécialisé dans la réalisation des examens radiologiques, en préparant les patients, en conduisant les équipements et en garantissant la qualité des images produites.', null, 4500000, 'Examens radiologiques, Préparation des patients, Conduite des équipements, Qualité des images', 46, 'Réaliser des examens radiologiques en garantissant la qualité des images'),
    ('Radiophysicien', 'Le radiophysicien est un expert en physique médicale spécialisé dans le contrôle de la qualité des appareils de radiologie, en assurant la sécurité des patients et du personnel, et en garantissant la précision des traitements radiologiques.', null, 8000000, 'Contrôle qualité, Appareils de radiologie, Sécurité des patients, Sécurité du personnel, Précision des traitements radiologiques', 46, 'Assurer la qualité et la sécurité des appareils de radiologie'),
    ('Assistant en radiologie', 'L’assistant en radiologie soutient les radiologues et les manipulateurs en radiologie dans la réalisation des examens radiologiques, en préparant les équipements, en accueillant les patients et en assurant le suivi administratif.', null, 3000000, 'Examens radiologiques, Préparation des équipements, Accueil des patients, Suivi administratif', 46, 'Soutenir les professionnels en radiologie dans la réalisation des examens'),
    ('Radiologiste interventionnel', 'Le radiologiste interventionnel est un médecin spécialisé dans les procédures d’imagerie médicale interventionnelle, en réalisant des gestes thérapeutiques sous guidage radiologique pour traiter certaines pathologies.', null, 9500000, 'Imagerie médicale interventionnelle, Gestes thérapeutiques, Guidage radiologique, Traitement des pathologies', 46, 'Réaliser des gestes thérapeutiques sous guidage radiologique'),
    ('Radiobiologiste', 'Le radiobiologiste est un expert en radiobiologie spécialisé dans l’étude des effets des rayonnements ionisants sur les tissus biologiques, en évaluant les risques et en proposant des mesures de protection adaptées.', null, 8500000, 'Radiobiologie, Rayonnements ionisants, Effets sur les tissus biologiques, Evaluation des risques, Mesures de protection', 46, 'Etudier les effets des rayonnements ionisants sur les tissus biologiques'),
    ('Radioprotectionniste', 'Le radioprotectionniste est un professionnel spécialisé dans la prévention des risques liés aux rayonnements ionisants, en mettant en place des mesures de protection pour les patients, le personnel médical et l’environnement.', null, 7500000, 'Prévention, Risques radiologiques, Mesures de protection, Patients, Personnel médical, Environnement', 46, 'Prévenir les risques liés aux rayonnements ionisants'),
    ('Radiographe', 'Le radiographe est un professionnel de santé spécialisé dans la réalisation des radiographies, en positionnant les patients, en réglant les appareils et en produisant des images de qualité pour aider au diagnostic médical.', null, 4000000, 'Radiographies, Positionnement des patients, Réglage des appareils, Images de qualité, Diagnostic médical', 46, 'Réaliser des radiographies de qualité pour le diagnostic médical'),
    ('Physicien médical', 'Le physicien médical est un expert en physique appliquée à la médecine, spécialisé dans la calibration des équipements radiologiques, la dosimétrie des rayonnements et la planification des traitements en radiothérapie.', null, 8500000, 'Physique médicale, Calibration des équipements, Dosimétrie des rayonnements, Planification des traitements, Radiothérapie', 46, 'Assurer la précision des équipements et des traitements radiologiques'),
    ('Radiologue pédiatrique', 'Le radiologue pédiatrique est un médecin spécialisé dans l’interprétation des images radiologiques chez les enfants, en réalisant des examens adaptés aux pathologies pédiatriques et en fournissant des diagnostics précis.', null, 8500000, 'Interprétation, Images radiologiques, Enfants, Examens adaptés, Diagnostics précis', 46, 'Interpréter les images radiologiques chez les enfants');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Chirurgien général', 'Le chirurgien général est un médecin spécialisé dans la réalisation d’interventions chirurgicales pour traiter différentes pathologies du corps humain, en utilisant des techniques chirurgicales avancées et en assurant le suivi post-opératoire des patients.', null, 10000000, 'Interventions chirurgicales, Pathologies du corps humain, Techniques chirurgicales avancées, Suivi post-opératoire', 47, 'Réaliser des interventions chirurgicales pour traiter différentes pathologies du corps humain'),
    ('Neurochirurgien', 'Le neurochirurgien est un chirurgien spécialisé dans le traitement chirurgical des pathologies du système nerveux central et périphérique, en réalisant des interventions délicates pour traiter les tumeurs cérébrales, les traumatismes crâniens, etc.', null, 15000000, 'Traitement chirurgical, Pathologies du système nerveux central et périphérique, Interventions délicates, Tumeurs cérébrales, Traumatismes crâniens', 47, 'Traiter chirurgicalement les pathologies du système nerveux central et périphérique'),
    ('Chirurgien cardiaque', 'Le chirurgien cardiaque est un spécialiste de la chirurgie cardiovasculaire, chargé de réaliser des interventions pour traiter les maladies cardiaques et vasculaires, en utilisant des techniques chirurgicales avancées pour effectuer des pontages coronariens, des remplacements de valves, etc.', null, 18000000, 'Chirurgie cardiovasculaire, Maladies cardiaques et vasculaires, Techniques chirurgicales avancées, Pontages coronariens, Remplacements de valves', 47, 'Réaliser des interventions chirurgicales pour traiter les maladies cardiaques et vasculaires'),
    ('Chirurgien orthopédiste', 'Le chirurgien orthopédiste est spécialisé dans le traitement chirurgical des pathologies du système musculo-squelettique, en réalisant des interventions pour corriger les fractures, les déformations osseuses, etc., et en utilisant des techniques de fixation interne ou externe.', null, 12000000, 'Traitement chirurgical, Pathologies du système musculo-squelettique, Fractures, Déformations osseuses, Techniques de fixation interne ou externe', 47, 'Traiter chirurgicalement les pathologies du système musculo-squelettique'),
    ('Chirurgien plastique et esthétique', 'Le chirurgien plastique et esthétique est spécialisé dans la réalisation d’interventions chirurgicales pour corriger ou améliorer l’apparence physique, en réalisant des rhinoplasties, des liftings, des liposuccions, etc., pour répondre aux demandes esthétiques des patients.', null, 14000000, 'Interventions chirurgicales, Correction, Amélioration, Apparence physique, Rhinoplasties, Liftings, Liposuccions', 47, 'Réaliser des interventions chirurgicales pour corriger ou améliorer l’apparence physique'),
    ('Chirurgien vasculaire', 'Le chirurgien vasculaire est spécialisé dans le traitement chirurgical des pathologies du système vasculaire, en réalisant des interventions pour traiter les anévrismes, les occlusions artérielles, etc., en utilisant des techniques de chirurgie vasculaire mini-invasives.', null, 16000000, 'Traitement chirurgical, Pathologies du système vasculaire, Anévrismes, Occlusions artérielles, Techniques de chirurgie vasculaire mini-invasives', 47, 'Traiter chirurgicalement les pathologies du système vasculaire'),
    ('Chirurgien maxillo-facial', 'Le chirurgien maxillo-facial est un spécialiste de la chirurgie de la face et de la bouche, chargé de traiter les traumatismes faciaux, les malformations cranio-maxillo-faciales, etc., en réalisant des interventions chirurgicales complexes.', null, 13000000, 'Chirurgie de la face et de la bouche, Traumatismes faciaux, Malformations cranio-maxillo-faciales, Interventions chirurgicales complexes', 47, 'Traiter chirurgicalement les pathologies de la face et de la bouche'),
    ('Chirurgien thoracique', 'Le chirurgien thoracique est spécialisé dans le traitement chirurgical des pathologies de la cage thoracique, en réalisant des interventions pour traiter les tumeurs pulmonaires, les infections pleurales, etc., en utilisant des techniques chirurgicales thoraciques avancées.', null, 17000000, 'Traitement chirurgical, Pathologies de la cage thoracique, Tumeurs pulmonaires, Infections pleurales, Techniques chirurgicales thoraciques avancées', 47, 'Traiter chirurgicalement les pathologies de la cage thoracique'),
    ('Chirurgien pédiatrique', 'Le chirurgien pédiatrique est spécialisé dans la réalisation d’interventions chirurgicales chez les enfants, en traitant diverses pathologies congénitales ou acquises, en réalisant des interventions délicates et en assurant le suivi post-opératoire.', null, 15000000, 'Interventions chirurgicales, Enfants, Pathologies congénitales, Pathologies acquises, Interventions délicates, Suivi post-opératoire', 47, 'Réaliser des interventions chirurgicales chez les enfants'),
    ('Chirurgien gastro-entérologue', 'Le chirurgien gastro-entérologue est spécialisé dans le traitement chirurgical des pathologies du tube digestif, en réalisant des interventions pour traiter les cancers digestifs, les maladies inflammatoires, etc., en utilisant des techniques chirurgicales avancées.', null, 16000000, 'Traitement chirurgical, Pathologies du tube digestif, Cancers digestifs, Maladies inflammatoires, Techniques chirurgicales avancées', 47, 'Traiter chirurgicalement les pathologies du tube digestif');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Biologiste médical', 'Le biologiste médical est un professionnel de la biologie clinique chargé d’analyser des échantillons biologiques (sang, urine, etc.) pour aider au diagnostic, au suivi thérapeutique et à la prévention des maladies, en utilisant des techniques d’analyse avancées.', null, 15000000, 'Analyse des échantillons biologiques, Diagnostic, Suivi thérapeutique, Prévention des maladies, Techniques d’analyse avancées', 48, 'Analyser des échantillons biologiques pour aider au diagnostic, au suivi thérapeutique et à la prévention des maladies'),
    ('Technicien de laboratoire médical', 'Le technicien de laboratoire médical est un professionnel spécialisé dans la réalisation d’analyses biologiques sous la supervision du biologiste médical, en suivant des protocoles stricts et en assurant la qualité des résultats.', null, 8000000, 'Analyses biologiques, Protocoles stricts, Qualité des résultats, Supervision', 48, 'Réaliser des analyses biologiques sous la supervision du biologiste médical'),
    ('Ingénieur biomédical', 'L’ingénieur biomédical est un expert en instrumentation médicale et en équipements biomédicaux, chargé de concevoir, d’installer et de maintenir des dispositifs médicaux dans les établissements de santé, en garantissant leur bon fonctionnement et leur conformité aux normes.', null, 12000000, 'Instrumentation médicale, Equipements biomédicaux, Conception, Installation, Maintenance, Dispositifs médicaux, Normes', 48, 'Concevoir, installer et maintenir des dispositifs médicaux dans les établissements de santé'),
    ('Technicien de laboratoire biomédical', 'Le technicien de laboratoire biomédical assiste les biologistes médicaux dans la réalisation des analyses biomédicales, en préparant les échantillons, en utilisant les équipements de laboratoire et en assurant la traçabilité des données.', null, 7000000, 'Analyses biomédicales, Préparation des échantillons, Equipements de laboratoire, Traçabilité des données', 48, 'Assister les biologistes médicaux dans la réalisation des analyses biomédicales'),
    ('Responsable qualité en biologie médicale', 'Le responsable qualité en biologie médicale est chargé de mettre en place et de maintenir un système de gestion de la qualité dans les laboratoires de biologie médicale, en veillant à la conformité des processus et à l’accréditation.', null, 10000000, 'Gestion de la qualité, Laboratoires de biologie médicale, Conformité des processus, Accréditation', 48, 'Mettre en place et maintenir un système de gestion de la qualité dans les laboratoires de biologie médicale'),
    ('Pharmacien biologiste', 'Le pharmacien biologiste est un professionnel de la santé spécialisé dans la biologie médicale, chargé de superviser les analyses biologiques, d’interpréter les résultats et de conseiller les prescripteurs sur les choix thérapeutiques.', null, 11000000, 'Supervision des analyses biologiques, Interprétation des résultats, Conseils thérapeutiques, Biologie médicale', 48, 'Superviser les analyses biologiques et conseiller les prescripteurs sur les choix thérapeutiques'),
    ('Technicien en génie biomédical', 'Le technicien en génie biomédical est spécialisé dans la maintenance préventive et curative des équipements médicaux, en assurant leur bon fonctionnement et en intervenant rapidement en cas de dysfonctionnement.', null, 8000000, 'Maintenance préventive, Maintenance curative, Equipements médicaux, Bon fonctionnement, Interventions en cas de dysfonctionnement', 48, 'Assurer la maintenance préventive et curative des équipements médicaux'),
    ('Chercheur en biologie clinique', 'Le chercheur en biologie clinique mène des travaux de recherche dans le domaine de la biologie médicale, en étudiant les mécanismes physiologiques et les biomarqueurs des maladies, afin de développer de nouvelles méthodes diagnostiques et thérapeutiques.', null, 13000000, 'Recherche, Biologie médicale, Mécanismes physiologiques, Biomarqueurs, Maladies, Méthodes diagnostiques, Méthodes thérapeutiques', 48, 'Mener des travaux de recherche dans le domaine de la biologie médicale'),
    ('Technicien en anatomopathologie', 'Le technicien en anatomopathologie assiste les anatomopathologistes dans la préparation et l’analyse des échantillons tissulaires, en utilisant des techniques histologiques et cytologiques pour aider au diagnostic des maladies.', null, 7000000, 'Préparation des échantillons tissulaires, Analyse des échantillons, Techniques histologiques, Techniques cytologiques, Diagnostic des maladies', 48, 'Assister les anatomopathologistes dans la préparation et l’analyse des échantillons tissulaires'),
    ('Ingénieur qualité en dispositifs médicaux', 'L’ingénieur qualité en dispositifs médicaux est chargé d’assurer la conformité des dispositifs médicaux aux réglementations en vigueur, en mettant en place des procédures de contrôle qualité et en réalisant des audits internes.', null, 10000000, 'Conformité des dispositifs médicaux, Réglementations, Procédures de contrôle qualité, Audits internes', 48, 'Assurer la conformité des dispositifs médicaux aux réglementations en vigueur');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Ophtalmologue', 'L’ophtalmologue est un médecin spécialisé dans le diagnostic, le traitement et la prévention des maladies et troubles de l’œil. Il réalise des examens de la vue, prescrit des traitements médicaux ou chirurgicaux, et assure le suivi des patients.', null, 20000000, 'Diagnostic des maladies oculaires, Traitement médical, Traitement chirurgical, Suivi des patients, Examen de la vue', 49, 'Réaliser des examens de la vue, prescrire des traitements médicaux ou chirurgicaux, et assurer le suivi des patients'),
    ('Orthoptiste', 'L’orthoptiste est un professionnel paramédical spécialisé dans la rééducation des troubles de la vision et des muscles oculaires. Il réalise des séances de rééducation visuelle et propose des exercices pour améliorer la coordination des yeux.', null, 8000000, 'Rééducation des troubles de la vision, Rééducation des muscles oculaires, Exercices de coordination', 49, 'Réaliser des séances de rééducation visuelle et proposer des exercices pour améliorer la coordination des yeux'),
    ('Opticien-lunetier', 'L’opticien-lunetier est un professionnel de la santé visuelle chargé de réaliser des examens de vue, de conseiller les patients dans le choix de montures et de verres correcteurs, et de fabriquer des lunettes sur mesure.', null, 6000000, 'Examens de vue, Conseils aux patients, Fabrication de lunettes, Montures, Verres correcteurs', 49, 'Réaliser des examens de vue, conseiller les patients dans le choix de montures et de verres correcteurs, et fabriquer des lunettes sur mesure'),
    ('Technicien d’ophtalmologie', 'Le technicien d’ophtalmologie assiste l’ophtalmologue dans la réalisation des examens de la vue et des mesures ophtalmologiques, en préparant le matériel et en assurant le confort des patients.', null, 7000000, 'Examens de la vue, Mesures ophtalmologiques, Préparation du matériel, Confort des patients', 49, 'Assister l’ophtalmologue dans la réalisation des examens de la vue et des mesures ophtalmologiques'),
    ('Chirurgien ophtalmologiste', 'Le chirurgien ophtalmologiste est un médecin spécialisé dans la réalisation d’interventions chirurgicales sur l’œil et ses annexes pour corriger les troubles de la vision, comme la cataracte ou la myopie.', null, 25000000, 'Interventions chirurgicales, Œil, Annexes oculaires, Cataracte, Myopie, Correction des troubles de la vision', 49, 'Réaliser des interventions chirurgicales sur l’œil et ses annexes pour corriger les troubles de la vision'),
    ('Infirmier en ophtalmologie', 'L’infirmier en ophtalmologie assiste les ophtalmologues dans la réalisation des examens et des soins ophtalmologiques, en préparant les patients, en effectuant des prélèvements et en assurant leur suivi.', null, 5000000, 'Examens ophtalmologiques, Soins ophtalmologiques, Préparation des patients, Prélèvements, Suivi des patients', 49, 'Assister les ophtalmologues dans la réalisation des examens et des soins ophtalmologiques'),
    ('Optométriste', 'L’optométriste est un professionnel de la santé visuelle spécialisé dans l’évaluation de la vision, la prescription de lentilles de contact et la détection des troubles oculaires, en utilisant des techniques d’optométrie avancées.', null, 7000000, 'Évaluation de la vision, Prescription de lentilles de contact, Détection des troubles oculaires, Optométrie avancée', 49, 'Évaluer la vision, prescrire des lentilles de contact et détecter les troubles oculaires'),
    ('Assistant ophtalmologique', 'L’assistant ophtalmologique assure le bon fonctionnement du cabinet ou du service d’ophtalmologie, en planifiant les rendez-vous, en accueillant les patients, en tenant à jour les dossiers médicaux et en coordonnant les activités.', null, 5000000, 'Gestion du cabinet, Accueil des patients, Tenue des dossiers médicaux, Coordination des activités', 49, 'Assurer le bon fonctionnement du cabinet ou du service d’ophtalmologie'),
    ('Orthopédiste', 'L’orthopédiste est un professionnel spécialisé dans la conception et la fabrication de dispositifs médicaux orthopédiques, tels que les prothèses oculaires ou les lentilles intraoculaires, pour corriger les déficiences visuelles.', null, 7000000, 'Conception, Fabrication, Dispositifs médicaux orthopédiques, Prothèses oculaires, Lentilles intraoculaires, Correction des déficiences visuelles', 49, 'Concevoir et fabriquer des dispositifs médicaux orthopédiques pour corriger les déficiences visuelles');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Radiothérapeute', 'Le radiothérapeute est un médecin spécialisé dans le traitement des cancers par radiothérapie, en planifiant et en délivrant des doses précises de rayonnements ionisants pour détruire les cellules cancéreuses tout en préservant les tissus sains.', null, 20000000, 'Traitement des cancers, Radiothérapie, Planification des doses, Rayonnements ionisants, Préservation des tissus sains', 50, 'Planifier et délivrer des doses précises de rayonnements ionisants pour traiter les cancers'),
    ('Physicien médical en radiothérapie', 'Le physicien médical en radiothérapie est responsable du contrôle qualité des équipements de radiothérapie, de la planification des traitements et du calcul des doses de rayonnement délivrées aux patients, en garantissant leur précision et leur sécurité.', null, 18000000, 'Contrôle qualité, Equipements de radiothérapie, Planification des traitements, Calcul des doses de rayonnement, Précision, Sécurité', 50, 'Contrôler la qualité des équipements de radiothérapie et planifier les traitements'),
    ('Manipulateur en électroradiologie médicale', 'Le manipulateur en électroradiologie médicale assiste le radiothérapeute dans la réalisation des séances de traitement par radiothérapie, en positionnant les patients, en réglant les équipements et en assurant leur confort.', null, 8000000, 'Radiothérapie, Positionnement des patients, Réglage des équipements, Confort des patients', 50, 'Assister le radiothérapeute dans la réalisation des séances de traitement par radiothérapie'),
    ('Dosimétriste', 'Le dosimétriste est spécialisé dans le calcul des doses de rayonnement à administrer aux patients en radiothérapie, en utilisant des logiciels de planification pour optimiser l’efficacité du traitement tout en limitant les effets indésirables.', null, 15000000, 'Calcul des doses de rayonnement, Radiothérapie, Logiciels de planification, Efficacité du traitement, Effets indésirables', 50, 'Calculer les doses de rayonnement à administrer aux patients en radiothérapie'),
    ('Technicien de maintenance en radiothérapie', 'Le technicien de maintenance en radiothérapie est chargé d’assurer le bon fonctionnement des équipements de traitement par radiothérapie, en réalisant des opérations de maintenance préventive et curative.', null, 10000000, 'Maintenance des équipements, Radiothérapie, Fonctionnement, Maintenance préventive, Maintenance curative', 50, 'Assurer le bon fonctionnement des équipements de traitement par radiothérapie'),
    ('Infirmier en radiothérapie', 'L’infirmier en radiothérapie prend en charge les patients lors des séances de radiothérapie, en les préparant physiquement et psychologiquement au traitement, en surveillant les effets secondaires et en assurant leur suivi.', null, 9000000, 'Préparation des patients, Surveiller les effets secondaires, Suivi des patients, Radiothérapie', 50, 'Prendre en charge les patients lors des séances de radiothérapie'),
    ('Assistant médical en radiothérapie', 'L’assistant médical en radiothérapie soutient l’équipe médicale dans l’organisation des séances de traitement par radiothérapie, en gérant l’agenda des patients, en assurant la coordination des soins et en fournissant un soutien administratif.', null, 7000000, 'Organisation des séances de traitement, Coordination des soins, Soutien administratif, Radiothérapie', 50, 'Soutenir l’équipe médicale dans l’organisation des séances de traitement'),
    ('Physicien en radiothérapie', 'Le physicien en radiothérapie est responsable du contrôle qualité des équipements de traitement par radiothérapie, de la calibration des appareils et de la simulation des traitements, en garantissant leur efficacité et leur sécurité.', null, 17000000, 'Contrôle qualité, Calibration des appareils, Simulation des traitements, Efficacité, Sécurité, Radiothérapie', 50, 'Contrôler la qualité des équipements et simuler les traitements'),
    ('Chargé d’affaires en radiothérapie', 'Le chargé d’affaires en radiothérapie est chargé de développer et de gérer des projets dans le domaine de la radiothérapie, en identifiant les besoins des clients, en proposant des solutions adaptées et en assurant le suivi des projets.', null, 13000000, 'Développement de projets, Gestion de projets, Radiothérapie, Identification des besoins, Proposition de solutions, Suivi des projets', 50, 'Développer et gérer des projets dans le domaine de la radiothérapie'),
    ('Ingénieur biomédical spécialisé en radiothérapie', 'L’ingénieur biomédical spécialisé en radiothérapie est chargé de concevoir, d’installer et de maintenir des équipements de traitement par radiothérapie, en assurant leur conformité aux normes et leur bon fonctionnement.', null, 16000000, 'Conception d’équipements, Installation d’équipements, Maintenance d’équipements, Conformité aux normes, Bon fonctionnement, Radiothérapie', 50, 'Concevoir, installer et maintenir des équipements de traitement par radiothérapie');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Pédiatre', 'Le pédiatre est un médecin spécialisé dans le diagnostic, le traitement et la prévention des maladies chez les enfants, depuis la naissance jusqu’à l’adolescence.', null, 20000000, 'Médecine pédiatrique, Diagnostic, Traitement, Prévention, Enfants, Adolescents', 51, 'Diagnostiquer, traiter et prévenir les maladies chez les enfants'),
    ('Infirmier pédiatrique', 'L’infirmier pédiatrique prend en charge les enfants malades ou blessés, en réalisant des soins spécifiques adaptés à leur âge et à leur pathologie, tout en apportant un soutien psychologique aux parents.', null, 8000000, 'Soins pédiatriques, Enfants, Soutien psychologique, Parents', 51, 'Prendre en charge les enfants malades ou blessés et apporter un soutien psychologique aux parents'),
    ('Pédiatre néonatalogiste', 'Le pédiatre néonatalogiste est spécialisé dans les soins aux nouveau-nés prématurés ou malades, en assurant leur suivi médical dès les premiers jours de vie et en coordonnant les équipes de soins.', null, 18000000, 'Soins néonatals, Nouveau-nés, Prématurés, Suivi médical, Coordination des équipes de soins', 51, 'Assurer les soins aux nouveau-nés prématurés ou malades'),
    ('Pédiatre allergologue', 'Le pédiatre allergologue est spécialisé dans le diagnostic et le traitement des allergies chez les enfants, en identifiant les allergènes responsables et en proposant des solutions adaptées à chaque cas.', null, 15000000, 'Allergies, Diagnostic, Traitement, Enfants, Allergènes', 51, 'Diagnostiquer et traiter les allergies chez les enfants'),
    ('Pédiatre gastro-entérologue', 'Le pédiatre gastro-entérologue est spécialisé dans les troubles digestifs chez les enfants, en réalisant des examens diagnostiques et en proposant des traitements adaptés aux pathologies gastro-intestinales.', null, 16000000, 'Troubles digestifs, Examens diagnostiques, Traitements, Enfants, Gastro-entérologie', 51, 'Prendre en charge les troubles digestifs chez les enfants'),
    ('Pédiatre pneumologue', 'Le pédiatre pneumologue est spécialisé dans les maladies respiratoires chez les enfants, en réalisant des examens respiratoires et en proposant des traitements pour les affections pulmonaires.', null, 17000000, 'Maladies respiratoires, Examens respiratoires, Traitements, Enfants, Pneumologie', 51, 'Diagnostiquer et traiter les maladies respiratoires chez les enfants'),
    ('Pédiatre cardiologue', 'Le pédiatre cardiologue est spécialisé dans les maladies cardiaques chez les enfants, en réalisant des examens cardiaques et en proposant des traitements pour les affections du cœur.', null, 19000000, 'Maladies cardiaques, Examens cardiaques, Traitements, Enfants, Cardiologie', 51, 'Diagnostiquer et traiter les maladies cardiaques chez les enfants'),
    ('Pédiatre endocrinologue', 'Le pédiatre endocrinologue est spécialisé dans les troubles hormonaux chez les enfants, en réalisant des examens endocriniens et en proposant des traitements pour les dysfonctionnements hormonaux.', null, 18000000, 'Troubles hormonaux, Examens endocriniens, Traitements, Enfants, Endocrinologie', 51, 'Prendre en charge les troubles hormonaux chez les enfants'),
    ('Pédiatre infectiologue', 'Le pédiatre infectiologue est spécialisé dans les maladies infectieuses chez les enfants, en réalisant des diagnostics précis et en proposant des traitements pour les infections bactériennes, virales ou parasitaires.', null, 17000000, 'Maladies infectieuses, Diagnostic, Traitement, Enfants, Infectiologie', 51, 'Diagnostiquer et traiter les maladies infectieuses chez les enfants'),
    ('Pédiatre oncologue', 'Le pédiatre oncologue est spécialisé dans les cancers pédiatriques, en réalisant des examens diagnostiques et en proposant des traitements adaptés pour les enfants atteints de cancer.', null, 20000000, 'Cancers pédiatriques, Diagnostic, Traitement, Enfants, Oncologie', 51, 'Prendre en charge les cancers pédiatriques'),
    ('Pédiatre neurologue', 'Le pédiatre neurologue est spécialisé dans les troubles neurologiques chez les enfants, en réalisant des examens neurologiques et en proposant des traitements pour les affections du système nerveux.', null, 19000000, 'Troubles neurologiques, Examens neurologiques, Traitements, Enfants, Neurologie', 51, 'Diagnostiquer et traiter les troubles neurologiques chez les enfants');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Chirurgien', 'Le chirurgien est un médecin spécialisé dans les interventions chirurgicales pour traiter les pathologies et les traumatismes, en utilisant des techniques opératoires adaptées à chaque cas.', null, 30000000, 'Chirurgie, Interventions chirurgicales, Pathologies, Traumatismes, Techniques opératoires', 52, 'Réaliser des interventions chirurgicales pour traiter les pathologies et les traumatismes'),
    ('Anesthésiste-réanimateur', 'L’anesthésiste-réanimateur est un médecin spécialisé dans la pratique de l’anesthésie pour permettre les interventions chirurgicales, ainsi que dans la prise en charge des patients en réanimation pour les maintenir en vie.', null, 28000000, 'Anesthésie, Réanimation, Interventions chirurgicales, Prise en charge des patients, Maintien en vie', 52, 'Pratiquer l’anesthésie et prendre en charge des patients en réanimation'),
    ('Chirurgien orthopédiste', 'Le chirurgien orthopédiste est spécialisé dans les interventions chirurgicales pour traiter les pathologies et les traumatismes du système musculo-squelettique, en utilisant des techniques spécifiques pour la chirurgie osseuse et articulaire.', null, 32000000, 'Chirurgie orthopédique, Pathologies musculo-squelettiques, Traumatismes, Techniques chirurgicales', 52, 'Réaliser des interventions chirurgicales pour traiter les pathologies du système musculo-squelettique'),
    ('Chirurgien cardiaque', 'Le chirurgien cardiaque est spécialisé dans les interventions chirurgicales pour traiter les pathologies cardiaques, en réalisant des opérations sur le cœur et les vaisseaux sanguins pour rétablir la circulation sanguine.', null, 35000000, 'Chirurgie cardiaque, Pathologies cardiaques, Opérations sur le cœur, Rétablissement de la circulation sanguine', 52, 'Réaliser des interventions chirurgicales pour traiter les pathologies cardiaques'),
    ('Chirurgien plasticien', 'Le chirurgien plasticien est spécialisé dans les interventions chirurgicales esthétiques et reconstructives pour corriger les défauts physiques et améliorer l’apparence des patients, en utilisant des techniques de chirurgie plastique.', null, 30000000, 'Chirurgie plastique, Interventions esthétiques, Interventions reconstructives, Techniques chirurgicales', 52, 'Réaliser des interventions chirurgicales esthétiques et reconstructives'),
    ('Chirurgien vasculaire', 'Le chirurgien vasculaire est spécialisé dans les interventions chirurgicales pour traiter les pathologies des vaisseaux sanguins, en réalisant des opérations pour rétablir la circulation sanguine dans le corps.', null, 33000000, 'Chirurgie vasculaire, Pathologies vasculaires, Opérations sur les vaisseaux sanguins, Rétablissement de la circulation sanguine', 52, 'Réaliser des interventions chirurgicales pour traiter les pathologies des vaisseaux sanguins'),
    ('Chirurgien gastro-entérologue', 'Le chirurgien gastro-entérologue est spécialisé dans les interventions chirurgicales pour traiter les pathologies du système digestif, en réalisant des opérations sur l’estomac, l’intestin et d’autres organes.', null, 32000000, 'Chirurgie gastro-intestinale, Pathologies digestives, Opérations sur l’estomac, l’intestin, Techniques chirurgicales', 52, 'Réaliser des interventions chirurgicales pour traiter les pathologies du système digestif'),
    ('Chirurgien urologue', 'Le chirurgien urologue est spécialisé dans les interventions chirurgicales pour traiter les pathologies des voies urinaires chez l’homme et la femme, en réalisant des opérations sur les reins, la vessie, la prostate et d’autres organes.', null, 33000000, 'Chirurgie urologique, Pathologies des voies urinaires, Opérations sur les reins, la vessie, la prostate, Techniques chirurgicales', 52, 'Réaliser des interventions chirurgicales pour traiter les pathologies des voies urinaires'),
    ('Chirurgien thoracique', 'Le chirurgien thoracique est spécialisé dans les interventions chirurgicales pour traiter les pathologies du thorax et des organes respiratoires, en réalisant des opérations sur les poumons, la plèvre et d’autres structures thoraciques.', null, 34000000, 'Chirurgie thoracique, Pathologies du thorax, Opérations sur les poumons, la plèvre, Techniques chirurgicales', 52, 'Réaliser des interventions chirurgicales pour traiter les pathologies du thorax'),
    ('Chirurgien maxillo-facial', 'Le chirurgien maxillo-facial est spécialisé dans les interventions chirurgicales pour traiter les pathologies du visage, de la mâchoire et de la cavité buccale, en réalisant des opérations de chirurgie faciale et de chirurgie dentaire.', null, 31000000, 'Chirurgie maxillo-faciale, Pathologies du visage, de la mâchoire, de la cavité buccale, Opérations de chirurgie faciale, dentaire', 52, 'Réaliser des interventions chirurgicales pour traiter les pathologies du visage, de la mâchoire et de la cavité buccale');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Pharmacologue', 'Le pharmacologue est un scientifique spécialisé dans l’étude des effets des médicaments sur le corps humain, en analysant leur pharmacocinétique et leur toxicologie pour garantir leur efficacité et leur sécurité.', null, 28000000, 'Pharmacologie, Médicaments, Effets pharmacocinétiques, Effets toxicologiques, Sécurité des médicaments', 53, 'Étudier les effets des médicaments sur le corps humain et garantir leur sécurité'),
    ('Toxicologue', 'Le toxicologue est un scientifique spécialisé dans l’étude des substances toxiques et de leurs effets sur les organismes vivants, en évaluant les risques toxiques et en proposant des mesures de prévention.', null, 27000000, 'Toxicologie, Substances toxiques, Effets toxiques, Risques toxiques, Mesures de prévention', 53, 'Étudier les effets des substances toxiques sur les organismes vivants et proposer des mesures de prévention'),
    ('Pharmacien clinicien', 'Le pharmacien clinicien est un professionnel de santé spécialisé dans la prise en charge pharmaceutique des patients en milieu hospitalier, en assurant le suivi des traitements, la gestion des médicaments et la prévention des risques médicamenteux.', null, 30000000, 'Pharmacie clinique, Prise en charge pharmaceutique, Suivi des traitements, Gestion des médicaments, Prévention des risques médicamenteux', 53, 'Assurer la prise en charge pharmaceutique des patients en milieu hospitalier'),
    ('Pharmacovigilant', 'Le pharmacovigilant est un professionnel chargé de surveiller et d’évaluer la sécurité des médicaments après leur mise sur le marché, en détectant et en analysant les effets indésirables pour garantir leur utilisation sûre.', null, 29000000, 'Pharmacovigilance, Sécurité des médicaments, Effets indésirables, Surveillance des médicaments, Analyse des risques', 53, 'Surveiller et évaluer la sécurité des médicaments après leur mise sur le marché'),
    ('Chercheur en pharmacologie', 'Le chercheur en pharmacologie est un scientifique spécialisé dans la recherche et le développement de nouveaux médicaments, en étudiant leur mode d’action, leur pharmacocinétique et leur toxicité pour améliorer les traitements existants.', null, 32000000, 'Recherche pharmacologique, Développement de médicaments, Mode d’action des médicaments, Pharmacocinétique, Toxicité des médicaments', 53, 'Contribuer à la recherche et au développement de nouveaux médicaments'),
    ('Expert en pharmacocinétique', 'L’expert en pharmacocinétique est un spécialiste chargé d’analyser et de modéliser le comportement des médicaments dans l’organisme, en évaluant leur absorption, leur distribution, leur métabolisme et leur élimination.', null, 31000000, 'Pharmacocinétique, Comportement des médicaments, Absorption, Distribution, Métabolisme, Élimination', 53, 'Analyser et modéliser le comportement des médicaments dans l’organisme'),
    ('Chercheur en toxicologie', 'Le chercheur en toxicologie est un scientifique spécialisé dans l’étude des effets des substances toxiques sur les organismes vivants, en recherchant leurs mécanismes d’action et en évaluant leurs risques pour la santé.', null, 33000000, 'Recherche toxicologique, Effets des substances toxiques, Mécanismes d’action, Risques pour la santé', 53, 'Étudier les effets des substances toxiques sur les organismes vivants'),
    ('Expert en pharmacotoxicologie', 'L’expert en pharmacotoxicologie est un spécialiste chargé d’évaluer les effets toxiques des médicaments sur l’organisme, en analysant leur potentiel de toxicité et en proposant des stratégies de prévention.', null, 32000000, 'Pharmacotoxicologie, Effets toxiques des médicaments, Potentiel de toxicité, Stratégies de prévention', 53, 'Évaluer les effets toxiques des médicaments sur l’organisme'),
    ('Biostatisticien', 'Le biostatisticien est un professionnel spécialisé dans l’analyse statistique des données en biologie et en médecine, en utilisant des méthodes statistiques pour interpréter les résultats des études cliniques et épidémiologiques.', null, 33000000, 'Biostatistique, Analyse statistique, Données biomédicales, Études cliniques, Études épidémiologiques', 53, 'Analyser statistiquement les données en biologie et en médecine'),
    ('Expert en évaluation des risques', 'L’expert en évaluation des risques est un spécialiste chargé d’évaluer les dangers et les risques liés à l’exposition aux substances toxiques, en analysant leur impact sur la santé humaine et l’environnement.', null, 31000000, 'Évaluation des risques, Dangers des substances toxiques, Impact sur la santé, Impact sur l’environnement', 53, 'Évaluer les dangers et les risques liés à l’exposition aux substances toxiques');
INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
    ('Professeur de littérature anglaise', 'Le professeur de littérature anglaise enseigne la littérature de langue anglaise dans les établissements d’enseignement supérieur, en préparant des cours, en animant des séminaires et en évaluant les connaissances des étudiants.', null, 35000000, 'Littérature anglaise, Enseignement, Préparation des cours, Animation des séminaires, Évaluation des connaissances', 54, 'Enseigner la littérature anglaise et évaluer les connaissances des étudiants'),
    ('Traducteur littéraire', 'Le traducteur littéraire est un professionnel chargé de traduire des œuvres littéraires de langue anglaise vers d’autres langues, en préservant leur style, leur ton et leur sens original.', null, 40000000, 'Traduction littéraire, Langue anglaise, Préservation du style, Préservation du ton, Préservation du sens', 54, 'Traduire des œuvres littéraires de langue anglaise vers d’autres langues'),
    ('Critique littéraire', 'Le critique littéraire est un spécialiste chargé d’analyser et d’évaluer des œuvres littéraires de langue anglaise, en rédigeant des articles, des essais ou des critiques pour les médias spécialisés.', null, 38000000, 'Critique littéraire, Analyse littéraire, Évaluation des œuvres, Rédaction d’articles, Rédaction d’essais', 54, 'Analyser et évaluer des œuvres littéraires de langue anglaise'),
    ('Écrivain public', 'L’écrivain public est un professionnel spécialisé dans la rédaction de documents officiels, administratifs ou personnels en langue anglaise, en aidant ses clients à formuler leurs idées de manière claire et concise.', null, 35000000, 'Rédaction de documents, Langue anglaise, Clarté, Concision, Service client', 54, 'Aider les clients à rédiger des documents en langue anglaise'),
    ('Éditeur de livres', 'L’éditeur de livres est un professionnel chargé de sélectionner, d’éditer et de publier des œuvres littéraires de langue anglaise, en collaborant avec les auteurs et en supervisant le processus de publication.', null, 42000000, 'Édition de livres, Sélection d’œuvres, Collaboration avec les auteurs, Supervision de la publication', 54, 'Sélectionner, éditer et publier des œuvres littéraires de langue anglaise'),
    ('Bibliothécaire spécialisé', 'Le bibliothécaire spécialisé est un professionnel chargé de gérer et de promouvoir des collections de livres de langue anglaise dans des bibliothèques ou des centres de documentation, en aidant les usagers dans leurs recherches.', null, 33000000, 'Gestion de collections, Promotion des livres, Langue anglaise, Aide à la recherche', 54, 'Gérer et promouvoir des collections de livres de langue anglaise'),
    ('Correcteur d’épreuves', 'Le correcteur d’épreuves est un spécialiste chargé de relire et de corriger des textes écrits en langue anglaise, en veillant à l’orthographe, à la grammaire et à la syntaxe.', null, 32000000, 'Correction d’épreuves, Langue anglaise, Orthographe, Grammaire, Syntaxe', 54, 'Relire et corriger des textes écrits en langue anglaise'),
    ('Animateur d’ateliers d’écriture', 'L’animateur d’ateliers d’écriture est un professionnel chargé d’animer des séances d’écriture créative en langue anglaise, en proposant des exercices et des conseils aux participants pour stimuler leur créativité.', null, 31000000, 'Ateliers d’écriture, Langue anglaise, Créativité, Animation, Conseils', 54, 'Animer des séances d’écriture créative en langue anglaise'),
    ('Consultant en communication écrite', 'Le consultant en communication écrite est un expert chargé d’aider les entreprises à rédiger des documents professionnels en langue anglaise, tels que des rapports, des présentations ou des supports de communication.', null, 36000000, 'Communication écrite, Rédaction de documents professionnels, Langue anglaise, Conseil aux entreprises', 54, 'Aider les entreprises à rédiger des documents professionnels en langue anglaise'),
    ('Scénariste', 'Le scénariste est un professionnel chargé d’écrire des scénarios pour des films, des séries télévisées ou des pièces de théâtre en langue anglaise, en développant des personnages, des dialogues et des intrigues.', null, 40000000, 'Écriture de scénarios, Langue anglaise, Développement de personnages, Développement de dialogues, Développement d’intrigues', 54, 'Écrire des scénarios pour des productions en langue anglaise');
