INSERT INTO metiers (nom, description, entreprisesrecrutent, salairemoyen, competencescles, Filieres_id, principales_missions)
VALUES 
    ('Cartographe', 'Le cartographe est un professionnel spécialisé dans la création de cartes géographiques. Son rôle principal est de collecter, analyser et représenter graphiquement des données géographiques pour produire des cartes précises et informatives. Les cartographes utilisent des outils technologiques avancés et ont une compréhension approfondie de la topographie et de la cartographie numérique.', NULL, '200000 à 300000', 'Compétences en cartographie numérique, maîtrise des logiciels SIG (Systèmes d’Information Géographique), capacité à interpréter des données géospatiales, précision et attention aux détails.', 58, 'Collecter et analyser des données géographiques, Créer et mettre à jour des cartes numériques, Effectuer des relevés de terrain, Réaliser des analyses spatiales, Produire des rapports et des présentations cartographiques'),
    ('Analyste SIG', 'L’analyste SIG (Système d’Information Géographique) est un professionnel spécialisé dans l’analyse et l’interprétation de données géographiques. Il utilise des logiciels SIG pour cartographier, modéliser et analyser des informations spatiales. L’analyste SIG travaille souvent avec des données provenant de diverses sources pour aider à la prise de décision dans des domaines tels que la planification urbaine, la gestion des ressources naturelles et l’analyse environnementale.', NULL, 'Entre 45000 et 60000', 'Maîtrise des logiciels SIG, capacité d’analyse spatiale, interprétation de données géospatiales, résolution de problèmes complexes liés à la cartographie.', 58, 'Collecter et traiter des données géographiques, Créer et mettre à jour des bases de données spatiales, Effectuer des analyses spatiales et géostatistiques, Développer des applications SIG, Produire des cartes et des rapports d’analyse'),
    ('Enseignant', 'L’enseignant en géographie est un professionnel de l’éducation spécialisé dans l’enseignement de la géographie. Il transmet des connaissances sur les divers aspects de la géographie physique, humaine et sociale. L’enseignant prépare des cours, crée des supports pédagogiques et évalue les étudiants. Il joue un rôle clé dans le développement des compétences géographiques des apprenants, les préparant à comprendre le monde qui les entoure.', NULL, 'Entre 40000 et 55000', 'Maîtrise des concepts géographiques, capacité à enseigner de manière interactive, compétences pédagogiques, communication efficace avec les élèves.', 58, 'Enseigner l’histoire et transmettre des connaissances, Préparer les cours et les supports pédagogiques, Évaluer les élèves, Encadrer des projets pédagogiques, Participer aux réunions pédagogiques, Assurer un suivi des élèves, Susciter l’intérêt pour l’histoire'),
    ('Chargé de projet environnementaux', 'Le chargé de projet environnementaux est un professionnel spécialisé dans la gestion et la coordination de projets liés à l’environnement. Il travaille sur des initiatives visant à protéger, restaurer et gérer les ressources naturelles. Ses responsabilités incluent la planification, la mise en œuvre et l’évaluation de projets environnementaux, ainsi que la collaboration avec différentes parties prenantes pour atteindre des objectifs durables.', NULL, 'Entre 50000 et 70000', 'Gestion de projets, expertise en environnement, communication interdisciplinaire, sensibilité aux enjeux environnementaux, capacité d’analyse géographique.', 58, 'Identifier et analyser les enjeux environnementaux, Concevoir et mettre en œuvre des projets de protection de l’environnement, Sensibiliser le public et les acteurs concernés, Rédiger des études d’impact et des rapports, Coordonner les équipes et les partenaires'),
    ('Planificateur Territorial', 'Le planificateur territorial en géographie est un professionnel spécialisé dans la conception, la gestion et la mise en œuvre de plans d’aménagement du territoire. Il collabore avec des experts en urbanisme, environnement, transport, et d’autres domaines connexes pour élaborer des projets qui répondent aux besoins de la collectivité tout en tenant compte des aspects environnementaux, sociaux et économiques. Son rôle consiste à assurer un développement territorial durable.', NULL, 'Entre 60000 et 80000', 'Planification urbaine, aménagement du territoire, gestion de projets, analyse géographique, communication interdisciplinaire.', 58, 'Élaborer des plans d’aménagement du territoire, Analyser les enjeux urbains, environnementaux et sociaux, Coordonner les différents acteurs impliqués, Organiser des concertations publiques, Veiller au respect des réglementations et des normes'),
    ('Expert en Environnement et Changements Climatiques', 'L’expert en environnement et changements climatiques travaille au sein d’organisations non gouvernementales (ONG) et d’organisations internationales dédiées à la lutte contre les changements climatiques. Son rôle consiste à mener des recherches, analyser les données environnementales, développer des stratégies et des politiques pour atténuer les impacts du changement climatique, et sensibiliser les populations à ces enjeux. Il participe également à des projets visant à promouvoir le développement durable et à réduire l’empreinte carbone.', NULL, 'Entre 70000 et 90000', 'Expertise en environnement, changements climatiques, gestion de projets environnementaux, analyse de données, sensibilisation, communication interculturelle.', 58, 'Évaluer les impacts environnementaux et climatiques, Concevoir des stratégies d’adaptation et d’atténuation, Mener des études et des recherches scientifiques, Sensibiliser les décideurs et le public, Participer à des négociations internationales');
INSERT INTO metiers (nom, description, entreprisesrecrutent, salairemoyen, competencescles, Filieres_id, principales_missions)
VALUES 
    ('Enseignant', 'Un enseignant est une personne chargée de transmettre des connaissances et de former des élèves ou des étudiants dans un établissement scolaire ou universitaire. Ses principales tâches consistent à préparer et dispenser des cours, évaluer les élèves, gérer la discipline en classe et participer à des réunions pédagogiques.', NULL, '250000', 'Connaissance approfondie de la matière enseignée, capacités pédagogiques, gestion de classe, communication orale et écrite', 59, 'Enseigner l’histoire et transmettre des connaissances, Préparer les cours et les supports pédagogiques, Évaluer les élèves, Encadrer des projets pédagogiques, Participer aux réunions pédagogiques, Assurer un suivi des élèves, Susciter l’intérêt pour l’histoire'),
    ('Archiviste', 'Un archiviste est un professionnel chargé de la collecte, de la conservation, du classement et de la mise à disposition des documents et archives historiques. Il veille à préserver l’intégrité des documents et à faciliter leur accessibilité pour la recherche et la consultation.', NULL, '300000', 'Connaissance de l’histoire et des techniques de conservation, compétences en gestion documentaire, capacité d’analyse et de synthèse', 59, 'Collecter, classer et conserver des archives, Assurer la gestion et la préservation des fonds d’archives, Faciliter l’accès aux archives et en assurer la valorisation, Mener des recherches historiques et documentaires, Participer à des expositions et des publications'),
    ('Historien', 'Un historien est un chercheur et un expert dans l’étude et l’analyse des événements, des civilisations et des cultures du passé. Il mène des recherches approfondies, analyse des sources primaires et secondaires, et produit des publications académiques ou des ouvrages historiques.', NULL, '400000', 'Connaissance approfondie de l’histoire, capacités de recherche et d’analyse, esprit critique, rédaction scientifique', 59, 'Mener des recherches historiques, Collecter et analyser des sources primaires, Rédiger des ouvrages et des articles scientifiques, Enseigner l’histoire, Participer à des colloques et des conférences, Valoriser le patrimoine historique'),
    ('Guide touristique', 'Un guide touristique est un professionnel chargé d’accompagner et d’informer les visiteurs sur les sites historiques, les monuments et les attractions culturelles d’une région. Il partage ses connaissances historiques et fournit des explications détaillées pour enrichir l’expérience des touristes.', NULL, '150000', 'Connaissances approfondies de l’histoire locale, compétences en communication orale, aptitudes à l’animation de groupe', 59, 'Guider des groupes de visiteurs, Présenter et commenter des lieux, des monuments ou des expositions, Effectuer des recherches historiques et documentaires, Concevoir des circuits et des visites guidées, Promouvoir le patrimoine touristique'),
    ('Conservateur de musée', 'Un conservateur de musée est responsable de la gestion, de la préservation et de la mise en valeur des collections d’un musée. Il supervise l’acquisition, la documentation, la restauration et l’exposition des œuvres et objets historiques. Il contribue également à la recherche et à l’éducation du public.', NULL, '500000', 'Expertise dans un domaine historique spécifique, connaissance des techniques de conservation, compétences en gestion de collections et en médiation culturelle', 59, 'Gérer et valoriser les collections d’un musée, Organiser des expositions temporaires et permanentes, Assurer la conservation des œuvres et des objets, Mener des recherches et des études, Concevoir des activités et des supports pédagogiques');
