INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
('Chercheur en biologie moléculaire', 'Le chercheur en biologie moléculaire étudie les processus biologiques au niveau moléculaire, notamment l’ADN, les protéines et les mécanismes cellulaires. Il réalise des expériences en laboratoire pour comprendre le fonctionnement des organismes vivants et développer de nouvelles thérapies ou technologies.', NULL, '4 500 000', 'Maîtrise des techniques de biologie moléculaire, capacité d’analyse et de synthèse, rigueur méthodologique, curiosité scientifique, esprit d’innovation.', 164, 'Conception et réalisation d’expériences, analyse des résultats, rédaction d’articles scientifiques, participation à des projets de recherche, veille bibliographique.'),
('Pharmacien biologiste', 'Le pharmacien biologiste exerce dans des laboratoires d’analyses médicales, des hôpitaux ou des entreprises pharmaceutiques. Il réalise des analyses biologiques (sang, urine, tissus) pour établir des diagnostics ou contrôler la qualité des médicaments. Il interprète les résultats et conseille les prescripteurs.', NULL, '3 800 000', 'Connaissance des techniques d’analyses biologiques, maîtrise des outils informatiques, compétences en communication, rigueur et précision, respect des règles d’hygiène et de sécurité.', 164, 'Prélèvement et analyse d’échantillons biologiques, interprétation des résultats, rédaction de comptes rendus, conseil aux prescripteurs, veille réglementaire.'),
('Ingénieur en biotechnologie', 'L’ingénieur en biotechnologie conçoit et développe des produits ou des procédés utilisant des organismes vivants ou des composants biologiques. Il travaille dans des domaines variés tels que l’agroalimentaire, la santé, l’environnement ou l’industrie pharmaceutique.', NULL, '3 600 000', 'Maîtrise des techniques de biologie moléculaire, compétences en génie génétique, créativité et esprit d’innovation, capacité à travailler en équipe, respect des normes de qualité et de sécurité.', 164, 'Conception et développement de produits biotechnologiques, optimisation des procédés de production, gestion de projet, veille technologique, collaboration avec des partenaires externes.'),
('Technicien de laboratoire en microbiologie', 'Le technicien de laboratoire en microbiologie réalise des analyses sur des échantillons biologiques pour détecter la présence de micro-organismes pathogènes ou contaminants. Il prépare les échantillons, effectue les tests et interprète les résultats sous la supervision d’un biologiste.', NULL, '1 800 000', 'Maîtrise des techniques de microbiologie, rigueur et précision, respect des protocoles et des normes de sécurité, capacité à travailler en équipe, organisation.', 164, 'Préparation des échantillons, réalisation des analyses microbiologiques, interprétation des résultats, rédaction de rapports, entretien du matériel.'),
('Enseignant-chercheur en biologie', 'L’enseignant-chercheur en biologie assure des cours théoriques et pratiques dans des établissements d’enseignement supérieur (universités, grandes écoles). Il mène également des travaux de recherche dans son domaine d’expertise et encadre des étudiants en master ou en doctorat.', NULL, '3 200 000', 'Maîtrise des connaissances en biologie, capacité à transmettre des savoirs, aptitude à la recherche et à l’innovation, encadrement pédagogique, communication scientifique.', 164, 'Préparation et animation des cours, encadrement des travaux pratiques, direction de mémoires et de thèses, participation à des projets de recherche, publication d’articles.'),
('Responsable assurance qualité en industrie pharmaceutique', 'Le responsable assurance qualité en industrie pharmaceutique veille au respect des normes de qualité et de sécurité dans la fabrication des médicaments. Il met en place et supervise les procédures de contrôle et d’assurance qualité tout au long du processus de production.', NULL, '4 000 000', 'Connaissance des réglementations pharmaceutiques, compétences en gestion de la qualité, capacité d’analyse et de résolution de problèmes, communication, rigueur.', 164, 'Élaboration et mise en place des procédures qualité, suivi des indicateurs de performance, audit des processus de production, gestion des non-conformités, collaboration avec les autorités sanitaires.'),
('Chef de projet en biologie marine', 'Le chef de projet en biologie marine pilote des études et des recherches dans le domaine de la biologie marine. Il coordonne les équipes scientifiques, définit les objectifs des projets et assure le suivi des travaux jusqu’à la publication des résultats.', NULL, '3 500 000', 'Expertise en biologie marine, gestion de projet, capacité à travailler en équipe pluridisciplinaire, organisation, communication.', 164, 'Élaboration du plan de recherche, coordination des activités des équipes, suivi du budget et des délais, rédaction de rapports scientifiques, présentation des résultats.'),
('Biostatisticien', 'Le biostatisticien analyse des données issues de la recherche en sciences de la vie (biologie, médecine, pharmacologie) pour en extraire des informations utiles à la prise de décision. Il conçoit les protocoles d’études, réalise les analyses statistiques et interprète les résultats.', NULL, '3 400 000', 'Maîtrise des méthodes statistiques, compétences en programmation et en utilisation de logiciels statistiques, rigueur méthodologique, capacité de synthèse et de communication.', 164, 'Conception des protocoles d’études, collecte et traitement des données, réalisation des analyses statistiques, interprétation des résultats, rédaction de rapports scientifiques.'),
('Technico-commercial en biotechnologie', 'Le technico-commercial en biotechnologie assure la promotion et la vente de produits ou de services biotechnologiques auprès d’une clientèle spécialisée (laboratoires de recherche, entreprises pharmaceutiques, industries agroalimentaires). Il conseille les clients, établit des devis et négocie les contrats.', NULL, '3 000 000', 'Connaissance des produits biotechnologiques, compétences commerciales, capacité à comprendre les besoins des clients, sens de la négociation, autonomie.', 164, 'Prospection commerciale, présentation des produits, suivi des clients, élaboration des offres commerciales, participation à des salons professionnels.'),
('Ingénieur écologue', 'L’ingénieur écologue étudie les écosystèmes naturels et les impacts des activités humaines sur l’environnement. Il participe à la conception et à la mise en œuvre de mesures de préservation de la biodiversité et de restauration des milieux naturels.', NULL, '3 300 000', 'Connaissance des écosystèmes et des interactions écologiques, compétences en gestion de projet, capacité d’analyse et de synthèse, sensibilisation à l’environnement, travail en équipe.', 164, 'Étude des écosystèmes, analyse des pressions anthropiques, proposition de mesures de préservation, suivi écologique des sites, sensibilisation à la biodiversité.');
