INSERT INTO Metiers (Nom, Description, EntreprisesRecrutent, SalaireMoyen, CompetencesCles, Filieres_id, principales_missions) VALUES
('Enseignant de langue anglaise', 'L’enseignant de langue anglaise dispense des cours d’anglais à des élèves de différents niveaux (primaire, collège, lycée, université). Il prépare les cours, anime les séances et évalue les élèves. Il peut également être amené à participer à des projets pédagogiques et à des activités extrascolaires.', NULL, '1 800 000', 'Maîtrise de la langue anglaise, pédagogie, patience, capacité à s’adapter aux besoins des élèves, organisation.', 170, 'Préparation des cours, animation des séances, évaluation des élèves, suivi individualisé, participation aux réunions pédagogiques.'),
('Traducteur-interprète', 'Le traducteur-interprète assure la traduction écrite ou orale de documents ou de discours d’une langue source vers la langue cible (ici, de l’anglais vers le français et vice versa). Il peut travailler dans différents domaines tels que la traduction littéraire, technique, juridique ou médicale.', NULL, '2 500 000', 'Maîtrise des langues source et cible, connaissance des domaines spécialisés, compétences rédactionnelles, capacité à travailler sous pression, respect des délais.', 170, 'Traduction de documents, interprétation lors de réunions ou de conférences, relecture et correction de textes, gestion de glossaires et de mémoires de traduction, respect des exigences de confidentialité.'),
('Responsable de communication internationale', 'Le responsable de communication internationale définit et met en œuvre la stratégie de communication d’une entreprise à l’échelle internationale. Il coordonne les actions de communication (communication interne, relations presse, événementiel, digital) pour promouvoir l’image et les produits de l’entreprise sur les marchés étrangers.', NULL, '4 000 000', 'Maîtrise de l’anglais, compétences en communication, créativité, capacité à travailler en réseau, gestion de projet.', 170, 'Définition de la stratégie de communication, coordination des actions de communication, rédaction de contenus, organisation d’événements, veille concurrentielle et sectorielle.'),
('Journaliste international', 'Le journaliste international couvre l’actualité à l’échelle mondiale pour des médias nationaux ou internationaux (presse écrite, radio, télévision, web). Il réalise des reportages, des interviews et des enquêtes sur des sujets politiques, économiques, sociaux ou culturels à l’étranger.', NULL, '3 500 000', 'Maîtrise de l’anglais, curiosité, esprit d’analyse et de synthèse, résistance au stress, mobilité géographique.', 170, 'Recherche et traitement de l’information, réalisation d’interviews et de reportages, rédaction d’articles, montage audiovisuel, respect des règles déontologiques.'),
('Consultant en relations internationales', 'Le consultant en relations internationales conseille les entreprises, les organisations gouvernementales ou les ONG sur les enjeux géopolitiques, économiques et diplomatiques à l’échelle mondiale. Il réalise des études de marché, des analyses de risques et formule des recommandations stratégiques.', NULL, '4 200 000', 'Connaissance des relations internationales, capacité d’analyse et de synthèse, compétences en conseil et en communication, adaptabilité, expertise sectorielle.', 170, 'Réalisation d’études de marché, analyse des risques géopolitiques, conseil stratégique, élaboration de recommandations, présentation des résultats.'),
('Agent de voyage spécialisé en tourisme linguistique', 'L’agent de voyage spécialisé en tourisme linguistique organise des séjours à l’étranger combinant cours de langue et découvertes culturelles. Il conseille les clients, établit des devis, réserve les prestations et assure le suivi des voyages.', NULL, '2 000 000', 'Maîtrise de l’anglais, connaissance des destinations et des cultures étrangères, compétences commerciales, sens de l’organisation, relationnel.', 170, 'Accueil et conseil des clients, conception de séjours sur mesure, réservation des prestations, suivi des voyages, gestion des litiges.'),
('Responsable de programme humanitaire', 'Le responsable de programme humanitaire gère des projets d’aide humanitaire dans des zones touchées par des crises (conflits armés, catastrophes naturelles, famines). Il évalue les besoins, définit les actions à mettre en place, coordonne les équipes sur le terrain et assure le suivi des projets.', NULL, '3 800 000', 'Connaissance des enjeux humanitaires, gestion de projet, capacité à travailler sous pression, aptitude au leadership, diplomatie.', 170, 'Évaluation des besoins sur le terrain, conception de projets humanitaires, coordination des équipes, suivi des actions, rapports d’activité et évaluation.'),
('Analyste financier international', 'L’analyste financier international analyse les marchés financiers mondiaux et évalue les risques et les opportunités d’investissement à l’échelle internationale. Il réalise des études sectorielles, suit les performances des entreprises et formule des recommandations en matière d’investissement.', NULL, '5 000 000', 'Maîtrise des techniques d’analyse financière, connaissance des marchés internationaux, capacité à anticiper les tendances, esprit d’analyse et de synthèse, réactivité.', 170, 'Analyse des marchés financiers, évaluation des risques et des opportunités, rédaction de rapports d’analyse, suivi des performances des entreprises, recommandations d’investissement.'),
('Linguiste informatique', 'Le linguiste informatique développe des outils et des applications informatiques liés au traitement automatique du langage naturel (traduction automatique, analyse sémantique, reconnaissance vocale). Il participe à la conception, au développement et à la maintenance de ces systèmes.', NULL, '4 500 000', 'Connaissance des langages de programmation, maîtrise des techniques de traitement automatique du langage, capacité d’analyse et de synthèse, curiosité intellectuelle, résolution de problèmes.', 170, 'Conception d’algorithmes linguistiques, développement d’applications informatiques, test et validation des systèmes, veille technologique, documentation technique.'),
('Spécialiste en communication interculturelle', 'Le spécialiste en communication interculturelle accompagne les entreprises ou les organisations dans leurs échanges avec des partenaires internationaux de cultures différentes. Il sensibilise les équipes aux spécificités culturelles, développe des outils de communication adaptés et favorise le dialogue interculturel.', NULL, '3 600 000', 'Connaissance des cultures étrangères, compétences en communication interpersonnelle, capacité à travailler en équipe multiculturelle, diplomatie, créativité.', 170, 'Analyse des spécificités culturelles, conception d’outils de communication, formation des équipes, médiation interculturelle, évaluation des actions.'),
('Agent consulaire', 'L’agent consulaire assure la protection et l’assistance des ressortissants de son pays à l’étranger. Il délivre des documents officiels (passeports, visas), assure un soutien en cas de difficultés (maladie, accident, arrestation) et facilite les démarches administratives.', NULL, '2 200 000', 'Connaissance des lois et des réglementations consulaires, compétences en relations publiques, sens de l’écoute et du service, réactivité, discrétion.', 170, 'Délivrance de documents officiels, assistance en cas d’urgence, représentation auprès des autorités locales, soutien aux ressortissants en difficulté, organisation d’événements consulaires.');
